    return {
      list: [],
      childrenList: [],
      listLoading: false,
      clearSearchButtonLoading: false,
      addDialogVisible: false,
      updateDialogVisible: false,
      generateDialogVisible: false,
      nameInput: "",
      linkInput: "",
      formLabelWidth: "120px",
      dialogTableVisible: false,
      currentPage: 1,
      //当前页
      mobileCurrentPage: 1,
      pagesizes: [50, 100, 200],
      //单页最大显示条数
