        name != "" ? paramsObj.name = name : "";
        uri != "" ? paramsObj.uri = uri : "";
        var orderList = [];
        var parentList = [];
        getMenuList(paramsObj).then(function (res) {
          _this.listTotal = res.data.total;
          var tableList = res.data.rows;
          tableList.forEach(function (tableItem) {
            var name = tableItem.name,
                uri = tableItem.uri,
                mode = tableItem.mode,
                sort = tableItem.sort,
                id = tableItem.id,
                parentId = tableItem.parentId;
            var orderItem = {
              name: name,
              linkAddress: uri,
              isUseful: mode,
              sort: sort,
              id: id,
              parentId: parentId
            };
            var parentItem = {
              label: name,
              value: id
            };
            parentList.push(parentItem);
            orderList.push(orderItem);
          });
          _this.list = orderList;
          _this.parentMenu = parentList;
        });
      }

      this.listLoading = false;
