      var orderList = [];
      updateMenuInquired().then(function (res) {
        var tableList = res.data;
        tableList.forEach(function (tableItem) {
          var name = tableItem.name,
              id = tableItem.id;
          var orderItem = {
            label: name,
            value: id
          };
          orderList.push(orderItem);
        });
      });
      this.parentMenu = orderList;
    },
    //表格高度自适应
    getHeight: function getHeight() {
      var otherHeight = this.device == "desktop" ? 316 : 200;
      this.tableMaxHeight = window.innerHeight - otherHeight;
    },
    // 单击复制
    handleUseful: function handleUseful(row, column, cell, event) {
      var _this2 = this;

      if (this.device == "mobile") return;

      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }

      this.clickFlag = setTimeout(function () {
        var count = 0;
        if (column.label == undefined) return;

        if (column.label == "操作" || column.label == "排序") {} else {
          var copyText = event.target.innerText;

          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");

            _this2.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            _this2.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    // 表格条数变化
    handleSizeChange: function handleSizeChange(val) {
      this.pagesize = val;
      this.getList();
    },
    //选择表格当前页数
    handleCurrentChange: function handleCurrentChange(val) {
      this.currentPage = val;
      this.getList();
    },
    // 获取子菜单数据
    getChildrenList: function getChildrenList() {},
    // 查看子菜单
    handleChildrenMenu: function handleChildrenMenu(row) {
      var _this3 = this;

      if (row.parentId != 0) return;
      this.childrenMenuRow = row;
      this.returnButton = true;
      this.currentPage = 1;
      this.pagesize = 50;
      this.childrenParamsOjb = {
        parentId: row.id,
        contains: false,
        page: this.currentPage,
        rows: this.pagesize
      };
      getMenuList(this.childrenParamsOjb).then(function (res) {
        var orderList = [];
        var tableList = res.data.rows;
        tableList.forEach(function (tableItem) {
          var name = tableItem.name,
              uri = tableItem.uri,
              mode = tableItem.mode,
              id = tableItem.id,
              parentId = tableItem.parentId,
              sort = tableItem.sort;
          var orderItem = {
            name: name,
            linkAddress: uri,
            isUseful: mode,
            id: id,
            parentId: parentId,
            sort: sort
          };
          orderList.push(orderItem);
        });
        _this3.list = orderList;
      });
    },
    // 返回主菜单列表
    handleReturnList: function handleReturnList() {
      this.returnButton = false;
      this.getList();
    },
    // 搜索
    handleSearch: function handleSearch() {
      this.searchButtonLoading = true;
      this.getList();
      this.searchButtonLoading = false;
    },
    // 清空搜索项
    handleClearSearch: function handleClearSearch() {
      this.nameInput = "";
      this.linkInput = "";
    },
    handleClick: function handleClick(row) {},
    // 新增
    handleAdd: function handleAdd() {
      this.addDialogVisible = true;
    },
