    addMenuVerify: function addMenuVerify() {
      if (this.addForm.parentMenu == null) {
        this.$message.error("请选择所属菜单");
        return false;
      }

      if (this.addForm.childrenName == "") {
        this.$message.error("请输入子菜单名");
        return false;
      }

      if (this.addForm.url == "") {
        this.$message.error("请输入Url");
        return false;
      }

      return true;
    },
    // 确认新增
    handleAddConfirm: function handleAddConfirm() {
      var _this4 = this;

