        this.$message.error("请输入Url");
        return;
      }

      if (this.addForm.url != "" && this.addForm.parentMenu == "") {
        this.$message.error("请选择所属菜单");
        return;
      }

      addMenu({
        name: this.addForm.childrenName,
        parentId: this.addForm.parentMenu,
        uri: this.addForm.url
      }).then(function (res) {
        if (res.status == 200) {
          _this4.getList();

          _this4.$message({
            type: "success",
            message: "新增成功"
          });
        }
