      this.addDialogVisible = false;
    },
    // 取消新增
    handleAddCancel: function handleAddCancel() {
      this.addDialogVisible = false;
    },
    // 更新
    handleUpdateClick: function handleUpdateClick(row) {
      this.updateForm.mode = row.isUseful;
      this.updateForm.childrenName = row.name;
      this.updateForm.url = row.linkAddress;
      row.parentId == 0 ? this.updateForm.parentMenu = "" : this.updateForm.parentMenu = row.parentId;
      this.updateId = row.id;
      this.updateDialogVisible = true;
    },
    // 验证更新
    updateMenuVerify: function updateMenuVerify() {
      if (this.updateForm.childrenName == "" && this.updateForm.url != "") {
        this.$message.error("请输入子菜单名");
        return false;
      }

      if (this.updateForm.url == "" && this.updateForm.childrenName != "") {
        this.$message.error("请输入Url");
        return false;
      }

      return true;
    },
    // 确认更新
    handleUpdateConfirm: function handleUpdateConfirm() {
      var _this5 = this;

      if (!this.updateMenuVerify) return;
      updateMenu({
        id: this.updateId,
        parentId: this.updateForm.parentMenu,
        name: this.updateForm.childrenName,
        uri: this.updateForm.url,
        mode: this.updateForm.mode
      }).then(function (res) {
        if (res.status == 200) {
          _this5.getList();

          _this5.$message({
            type: "success",
            message: "更新成功"
          });
        }
      }).catch(function (error) {});
      this.updateDialogVisible = false;
    },
    // 取消更新
    handleUpdateCancel: function handleUpdateCancel() {
      this.updateDialogVisible = false;
    },
