    handleDeleteClick: function handleDeleteClick(row) {
      var _this6 = this;

      this.$confirm("此操作将永久删除该菜单, 是否继续?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(function () {
        _this6.listLoading = true;
        deleteMenu({
          id: row.id
        }).then(function (res) {
          if (res.status === 200) {
            _this6.getList();

            _this6.$message({
              type: "success",
              message: "删除菜单成功"
            });

            _this6.dialogTableVisible = false;
          } else {
            _this6.$message.error("删除失败");

            _this6.dialogTableVisible = false;
          }
        });
        _this6.listLoading = false;
      }).catch(function (error) {
        _this6.$message({
          type: "info",
          message: "已取消删除"
        });

        _this6.dialogTableVisible = false;
      });
    },
    // 批量删除
    handleDeleteSelect: function handleDeleteSelect() {
      var _this7 = this;

      this.$confirm("此操作将永久删除该菜单, 是否继续?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      }).then(function () {
        if (_this7.multipleSelection.length === 0) {
          _this7.$message.error("未选择数据");

          return;
        }

        var ids = [];

        _this7.multipleSelection.forEach(function (selectItem) {
          ids.push(selectItem.id);
        });

        var idsStr = ids.join(",");
        deleteSelectMenu({
          ids: idsStr
        }).then(function (res) {
          if (res.status === 200) {
            _this7.getList();

            _this7.$message({
              type: "success",
              message: "批量删除菜单成功"
            });
          } else {
            _this7.$message.error("删除失败");
          }
        });
      }).catch(function () {
        _this7.$message({
          type: "info",
          message: "已取消删除"
        });
      });
    },
    // 一键生成
    handleGenerate: function handleGenerate() {
      this.generateDialogVisible = true;
    },
    // 验证生成
    generateVerify: function generateVerify() {
      if (this.generateForm.chineseName == "") {
        this.$message.error("请输入中文名");
        return false;
      }

      if (this.generateForm.englishName == "") {
        this.$message.error("请输入英文名");
        return false;
      }

      return true;
    },
    // 确认生成
    handleGenerateConfirm: function handleGenerateConfirm() {
      if (!this.generateVerify()) return;
      generateMenu({
        chinese: this.generateForm.chineseName,
        english: this.generateForm.englishName
      }).then(function (res) {});
    },
    // 取消生成
    handleGenerateCancel: function handleGenerateCancel() {},
    // 菜单上移
    handleMoveUpClick: function handleMoveUpClick(row) {
      var _this8 = this;

      var timestamp = new Date().getTime();
      moveUpMenu({
        sort: row.sort,
        parentId: row.parentId,
        id: row.id,
        _: timestamp
      }).then(function (res) {
        if (res.status == 200) {
          _this8.getList();

          _this8.$message({
            type: "success",
            message: "操作成功"
          });
        }
      });
    },
    // 菜单下移
    handleMoveDownClick: function handleMoveDownClick(row) {
      var _this9 = this;

      var timestamp = new Date().getTime();
      moveDownMenu({
        sort: row.sort,
        parentId: row.parentId,
        id: row.id,
        _: timestamp
      }).then(function (res) {
        if (res.status == 200) {
          _this9.getList();

          _this9.$message({
            type: "success",
            message: "操作成功"
          });
        }
      });
    },
