import Vue from "vue";
import XEUtils from "xe-utils";
import permission from "@/directive/permission/index.js"; // 权限判断指令

import { getMonitorProductList, importMonitorProduct } from "@/api/orderList";
import { parseTime } from "@/utils";
import { setTimeout, clearTimeout } from "timers";
Vue.use(_Pagination);
Vue.use(_Button);
Vue.use(_Popup);
Vue.use(_NavBar);
Vue.use(_Cell);
Vue.use(_CellGroup);
Vue.use(_DatetimePicker);
Vue.use(_Picker);
Vue.use(_Field);
Vue.use(_Icon);
Vue.use(_Toast);
Vue.use(_ActionSheet);
Vue.use(_Search);
export default {
  name: "system-monitorProduct",
  directives: {
    permission: permission
  },
  data: function data() {
    return {
      list: [],
      formLabelWidth: "120px",
      SearchButtonLoading: false,
      clearSearchButtonLoading: false,
      importProdcutLoading: false,
      listLoading: false,
      currentPage: 1,
      //当前页
      mobileCurrentPage: 1,
      pagesizes: [500, 1000, 2000],
      //单页最大显示条数
      pagesize: 500,
      //单页内条数
      listTotal: 0,
      //总数
      clickFlag: null,
      // 单击定时器
      mobileSearchShow: false,
      mobileSearchButtonLoading: false,
      tableMaxHeight: 0,
      pageJumpIndex: 1,
      nameInput: "",
      nameMobileValue: "",
      dialogVisible: false,
      urlInput: "",
      fileList: [],
      importLoading: false,
      baseUrl: ""
    };
  },
  created: function created() {
    this.getList();
    this.device = this.$store.state.app.device;
    window.addEventListener("resize", this.getHeight);
    this.getHeight();
    this.baseUrl = process.env.VUE_APP_BASE_API;
  },
  destroyed: function destroyed() {
    window.removeEventListener("resize", this.getHeight);
  },
  computed: {
    deviceVal: function deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal: function deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    // 获取数据
    getList: function getList() {
      var _this = this;

      this.listLoading = true;
      var searchList = [];
      var paramsObj = {
        contains: false,
        page: this.currentPage,
        rows: this.pagesize
      };
      this.nameInput == "" ? paramsObj.name = this.nameInput : "";
      getMonitorProductList(paramsObj).then(function (res) {
        _this.listTotal = res.data.total;
        var tableList = res.data.rows;
        tableList.forEach(function (tableItem) {
          var createTime = tableItem.createTime,
              img = tableItem.img,
              name = tableItem.name,
              numbering = tableItem.numbering,
              oneUp = tableItem.oneUp,
              threeUp = tableItem.threeUp,
              sixUp = tableItem.sixUp,
              twelveUp = tableItem.twelveUp,
              twentyfourUp = tableItem.twentyfourUp,
              twentyfourPercentage = tableItem.twentyfourPercentage,
              sellNum = tableItem.sellNum,
              shopId = tableItem.shopId,
              shopName = tableItem.shopName,
              skuMinPrice = tableItem.skuMinPrice,
              status = tableItem.status,
              text = tableItem.text,
              updateTime = tableItem.updateTime,
              url = tableItem.url;
          var orderItem = {
            productName: name,
            storeName: shopName,
            productMainImageUrl: img,
            minPrice: skuMinPrice,
            createTime: createTime,
            updateTime: updateTime,
            count: sellNum,
            withInOneHour: oneUp,
            withInThreeHour: threeUp,
            withInSixHour: sixUp,
            withInTwelveHour: twelveUp,
            yestoday: twentyfourUp,
            growthRate: twentyfourPercentage,
            linkAddress: text,
            linkChannel: url
          };
          searchList.push(orderItem);
        });
        _this.list = searchList;
      });
      setTimeout(function () {
        _this.listLoading = false;
      }, 1000);
    },
    //表格高度自适应
    getHeight: function getHeight() {
      var _this2 = this;

      this.$nextTick(function () {
        if (_this2.device === "desktop") {
          _this2.tableMaxHeight = document.body.offsetHeight - (200 + _this2.$refs.filterBox.offsetHeight + 40 + 18);
        } else {
          _this2.tableMaxHeight = document.body.offsetHeight - (100 + 40 + 40 + 88 + 10);
        }
      });
    },
    //单击复制
    handleUseful: function handleUseful(row, column, cell, event) {
      var _this3 = this;

      if (this.device == "mobile") return;

      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }

      this.clickFlag = setTimeout(function () {
        var count = 0;
        if (column.label == undefined) return;

        if (column.label == "链接地址") {
          window.open(row.linkChannel);
        } else {
          var copyText = event.target.innerText;

          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");

            _this3.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            _this3.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    //搜索
    handleSearch: function handleSearch() {
      if (this.nameInput == "") {
        this.$message.error("请输入产品名称");
        return;
      }

      this.SearchButtonLoading = true;
      this.getList();
      this.SearchButtonLoading = false;
    },
    handleClearSearch: function handleClearSearch() {},
    //选择表格尺寸
    handleSizeChange: function handleSizeChange(val) {
      this.pagesize = val;
      this.getList();
    },
    //选择表格当前页数
    handleCurrentChange: function handleCurrentChange(val) {
      this.currentPage = val;
      this.getList();
    },
    //导入产品
    handleImportProduct: function handleImportProduct() {
      this.dialogVisible = true;
    },
    // 确认选择
    submitUpload: function submitUpload() {
      this.$refs.upload.submit();
      this.dialogVisible = false;
    },
    // 选择文件
    handlePreview: function handlePreview(file) {},
    // 移除选择
    handleRemove: function handleRemove(file, fileList) {},
    // 关闭
    handleClose: function handleClose() {
      this.dialogVisible = false;
    },
    // 导入商品
    handleImportProductByUrl: function handleImportProductByUrl() {
      var _this4 = this;

      if (this.urlInput == "") {
        this.$message.error("请输入URL地址");
        return;
      }

      this.importLoading = true;
      importMonitorProduct({
        url: this.urlInput
      }).then(function (res) {
        if (res.status == 200) {
          _this4.dialogVisible = false;
          _this4.importLoading = false;

          _this4.$alert(res.data.replace(/;/g, "<br>"), "导入结果", {
            dangerouslyUseHTMLString: true,
            confirmButtonText: "确定",
            callback: function callback(action) {}
          });
        }
      });
    },

    /* 移动端事件 */
    //获取数据
    getMobileList: function getMobileList() {
      var _this5 = this;

      this.listLoading = true;
      var searchList = [];
      var paramsObj = {
        contains: false,
        page: this.mobileCurrentPage,
        rows: this.pagesize
      };
      this.nameMobileValue != "" ? paramsObj.name = this.nameMobileValue : "";
      getMonitorProductList(paramsObj).then(function (res) {
        _this5.listTotal = res.data.total;
        var tableList = res.data.rows;
        tableList.forEach(function (tableItem) {
          var createTime = tableItem.createTime,
              img = tableItem.img,
              name = tableItem.name,
              numbering = tableItem.numbering,
              oneUp = tableItem.oneUp,
              threeUp = tableItem.threeUp,
              sixUp = tableItem.sixUp,
              twelveUp = tableItem.twelveUp,
              twentyfourUp = tableItem.twentyfourUp,
              twentyfourPercentage = tableItem.twentyfourPercentage,
              sellNum = tableItem.sellNum,
              shopId = tableItem.shopId,
              shopName = tableItem.shopName,
              skuMinPrice = tableItem.skuMinPrice,
              status = tableItem.status,
              text = tableItem.text,
              updateTime = tableItem.updateTime,
              url = tableItem.url;
          var orderItem = {
            productName: name,
            storeName: shopName,
            productMainImageUrl: img,
            minPrice: skuMinPrice,
            createTime: createTime,
            updateTime: updateTime,
            count: sellNum,
            withInOneHour: oneUp,
            withInThreeHour: threeUp,
            withInSixHour: sixUp,
            withInTwelveHour: twelveUp,
            yestoday: twentyfourUp,
            growthRate: twentyfourPercentage,
            linkAddress: text,
            linkChannel: url
          };
          searchList.push(orderItem);
        });
        _this5.list = searchList;
      });
      setTimeout(function () {
        _this5.listLoading = false;
      }, 1000);
    },
    //分页器
    handlePageChange: function handlePageChange() {
      this.getMobileList();
    },
    //点击搜索
    handleSearchMobile: function handleSearchMobile() {
      if (!this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    //取消搜索
    handleSearchMobileCancel: function handleSearchMobileCancel() {
      if (this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    //清空所选
    handleSearchMobileClearAll: function handleSearchMobileClearAll() {
      this.nameMobileValue == "" ? "" : this.nameMobileValue = "";
    },
    //开始搜索
    handleMobileSearch: function handleMobileSearch() {
      if (this.nameMobileValue == "") {
        this.$message.error("请输入产品名称");
        return;
      }

      this.mobileSearchButtonLoading = true;
      this.getMobileList();
      this.mobileSearchButtonLoading = false;
      this.mobileSearchShow = false;
    },
    //限制页面跳转输入框只能输入数字
    jumpPageInput: function jumpPageInput() {
      this.pageJumpIndex = this.pageJumpIndex.replace(/[^\d]/g, "");
    },
    //跳转指定页面
    handleJumpPage: function handleJumpPage() {
      var jumpPage = parseInt(this.pageJumpIndex);
      if (jumpPage == this.mobileCurrentPage) return;

      if (jumpPage > Math.ceil(this.listTotal / this.pagesize)) {
        jumpPage = Math.ceil(this.listTotal / this.pagesize);
      }

      if (jumpPage < 1) {
        jumpPage = 1;
      }

      this.pageJumpIndex = jumpPage;
      this.currentPage = jumpPage;
      this.getMobileList();
    }
  }
};
