import Vue from "vue";
import { getWechatUserList, sendWechatMessage } from "@/api/orderList";
import { parseTime } from "@/utils";
import { setTimeout, clearTimeout } from "timers";
Vue.use(_Pagination);
Vue.use(_Button);
Vue.use(_Popup);
Vue.use(_NavBar);
