Vue.use(_Icon);
Vue.use(_Toast);
Vue.use(_ActionSheet);
Vue.use(_Search);
export default {
  name: "weChatMini-user",
  data: function data() {
    return {
      list: [],
      pickerOptions: {
        shortcuts: [{
          text: "上月",
          onClick: function onClick(picker) {
            var start = new Date(new Date().getFullYear(), new Date().getMonth() - 1, 1);
            var end = new Date(new Date().getFullYear(), new Date().getMonth() - 1, new Date(new Date().getFullYear(), new Date().getMonth(), 0).getDate());
            picker.$emit("pick", [start, end]);
          }
        }]
      },
      miniprogramOptions: [{
        value: "1",
        label: "劳斯宾"
      }, {
        value: "2",
        label: "掌上淘货"
      }, {
        value: "3",
        label: "一贝聚站"
      }],
      MiniProgramColumns: ["劳斯宾", "掌上淘货", "一贝聚站"],
      filterOptions: [{
        value: "1",
        label: "筛选"
      }],
      timeSelectValue: "",
      filterValue: "1",
      nameInput: "",
      phoneNumberInput: "",
      miniprogramValue: "",
      listLoading: false,
      currentPage: 1,
      //当前页
      mobileCurrentPage: 1,
      pagesizes: [100, 500, 2000],
      //单页最大显示条数
      pagesize: 100,
      //单页内条数
      listTotal: 0,
      //总数
      multipleSelection: [],
      clickFlag: null,
      // 单击定时器
      mobileSearchShow: false,
      mobileDatePickerShow: false,
      mobileMiniProgramPickerShow: false,
      minDate: new Date(1950, 10, 1),
      maxDate: new Date(),
      currentDate: new Date(),
      minDateEnd: new Date(),
      maxDateStart: new Date(),
      timePickerStartValue: "请选择",
      timePickerEndValue: "请选择",
      miniProgramMobileValue: "请选择",
      nameMobileValue: "",
      phoneMobileValue: "",
      device: "",
      datetimePickerType: "",
      mobileSearchButtonLoading: false,
      searchButtonLoading: false,
      tableMaxHeight: 0,
      pageJumpIndex: 1
    };
  },
  created: function created() {
    this.getList();
    this.device = this.$store.state.app.device;
    window.addEventListener("resize", this.getHeight);
    this.getHeight();
  },
  destroyed: function destroyed() {
    window.removeEventListener("resize", this.getHeight);
  },
  computed: {
    deviceVal: function deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal: function deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    // 获取数据
    getList: function getList() {
      var _this = this;

      this.listLoading = true;
      var searchList = [];
      var paramsObj = {
        rows: this.pagesize,
        page: this.currentPage
      };

      if (this.timeSelectValue == null) {
        this.timeSelectValue = ["", ""];
      } else {
        this.timeSelectValue == "" ? this.timeSelectValue : ["", ""];
      }

      this.timeSelectValue[0] ? paramsObj.createTime = this.timeSelectValue[0] : "";
      this.timeSelectValue[1] ? paramsObj.createTimeEnd = this.timeSelectValue[1] : "";
      this.nameInput ? paramsObj.name = this.nameInput : "";
      this.phoneNumberInput ? paramsObj.telephone = this.phoneNumberInput : "";
      this.miniprogramValue ? paramsObj.state = this.miniprogramValue : "";
      this.filterValue ? paramsObj.isRepeat = this.filterValue : "";
      getWechatUserList(paramsObj).then(function (res) {
        _this.listTotal = res.data.total;
        var tableData = res.data.rows;
        tableData.forEach(function (tableItem) {
          var id = tableItem.id,
              nickName = tableItem.nickName,
              phone = tableItem.phone,
              formId = tableItem.formId,
              avatarUrl = tableItem.avatarUrl,
              gender = tableItem.gender,
              country = tableItem.country,
              province = tableItem.province,
              city = tableItem.city,
              language = tableItem.language,
              createTime = tableItem.createTime,
              overTime = tableItem.overTime;
          var orderItem = {
            id: id,
            name: nickName,
            phoneNumber: phone,
            formId: formId,
            avatarUrl: avatarUrl,
            gender: gender,
            country: country,
            province: province,
            city: city,
            language: language,
            createTime: createTime,
            maturityTime: overTime
          };
          searchList.push(orderItem);
        });
        _this.list = searchList;
      }).catch(function (error) {});
      setTimeout(function () {
        _this.listLoading = false;
      }, 1000);
    },
    //表格高度自适应
    getHeight: function getHeight() {
      var _this2 = this;

      this.$nextTick(function () {
        if (_this2.device === "desktop") {
          _this2.tableMaxHeight = document.body.offsetHeight - (200 + _this2.$refs.filterBox.offsetHeight + 40 + 18);
        } else {
          _this2.tableMaxHeight = document.body.offsetHeight - (100 + 40 + 40 + 88 + 10);
        }
      });
    },
    //单击复制
    handleUseful: function handleUseful(row, column, cell, event) {
      var _this3 = this;

      if (this.device == "mobile") return;

      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }

      this.clickFlag = setTimeout(function () {
        var count = 0;
        if (column.label == undefined) return;

        if (column.label == "操作") {
          _this3.list.forEach(function (item) {
            if (item.id == row.id) {} else {
              count++;
            }
          });
        } else {
          var copyText = event.target.innerText;

          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");

            _this3.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            _this3.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    //搜索
    handleSearch: function handleSearch() {
      this.searchButtonLoading = true;
      this.getList();
      this.searchButtonLoading = false;
    },
    //选择表格尺寸
    handleSizeChange: function handleSizeChange(val) {
      this.pagesize = val;
      this.getList();
    },
    //选择表格当前页数
    handleCurrentChange: function handleCurrentChange(val) {
      this.currentPage = val;
      this.getList();
    },
    //选择发生改变
    handleSelectChange: function handleSelectChange(selection) {
      this.multipleSelection = selection;
    },
    // 发送消息
    handleSendMessage: function handleSendMessage(rows) {
      var id = rows.id;
      sendWechatMessage({
        id: id
      }).then(function (res) {});
    },

    /* 移动端事件 */
    //获取数据
    getMobileList: function getMobileList() {
      var _this4 = this;

      this.listLoading = true;
      var searchList = [];
      var miniProgramMobile = "";
      var timeStartValue = "";
      var timeEndValue = "";
      var paramsObj = {
        rows: this.pagesize,
        page: this.mobileCurrentPage
      };
      this.timePickerStartValue == "请选择" ? timeStartValue = "" : timeStartValue = this.timePickerStartValue.replace(/\//g, "-");
      this.timePickerEndValue == "请选择" ? timeEndValue = "" : timeEndValue = this.timePickerEndValue.replace(/\//g, "-");
      this.miniprogramOptions.forEach(function (item) {
        if (item.label == _this4.miniProgramMobileValue) {
          miniProgramMobile = item.value;
        }
      });
      timeStartValue != "" ? paramsObj.createTime = timeStartValue : "";
      timeEndValue != "" ? paramsObj.createTimeEnd = timeEndValue : "";
      miniProgramMobile != "" ? paramsObj.state = miniProgramMobile : "";
      this.nameMobileValue != "" ? paramsObj.name = this.nameMobileValue : "";
      this.phoneMobileValue != "" ? paramsObj.telephone = this.phoneMobileValue : "";
      paramsObj.isRepeat = this.filterValue;
      getWechatUserList(paramsObj).then(function (res) {
        _this4.listTotal = res.data.total;
        var tableData = res.data.rows;
        tableData.forEach(function (tableItem) {
          var id = tableItem.id,
              nickName = tableItem.nickName,
              phone = tableItem.phone,
              formId = tableItem.formId,
              avatarUrl = tableItem.avatarUrl,
              gender = tableItem.gender,
              country = tableItem.country,
              province = tableItem.province,
              city = tableItem.city,
              language = tableItem.language,
              createTime = tableItem.createTime,
              overTime = tableItem.overTime;
          var orderItem = {
            id: id,
            name: nickName,
            phoneNumber: phone,
            formId: formId,
            avatarUrl: avatarUrl,
            gender: gender,
            country: country,
            province: province,
            city: city,
            language: language,
            createTime: createTime,
            maturityTime: overTime
          };
          searchList.push(orderItem);
        });
        _this4.list = searchList;
      }).catch(function (error) {});
      setTimeout(function () {
        _this4.listLoading = false;
      }, 1000);
    },
    //分页器
    handlePageChange: function handlePageChange() {
      this.getMobileList();
    },
    //点击搜索
    handleSearchMobile: function handleSearchMobile() {
      if (!this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    //取消搜索
    handleSearchMobileCancel: function handleSearchMobileCancel() {
      if (this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    //清空所选
    handleSearchMobileClearAll: function handleSearchMobileClearAll() {
      this.timePickerStartValue == "请选择" ? "" : this.timePickerStartValue = "请选择", this.timePickerEndValue == "请选择" ? "" : this.timePickerEndValue = "请选择", this.nameMobileValue == "" ? "" : this.nameMobileValue = "", this.phoneMobileValue == "" ? "" : this.phoneMobileValue = "";
    },
    //选择开始日期
    handleChooseDateStartMobile: function handleChooseDateStartMobile() {
      if (!this.mobileDatePickerShow) {
        if (this.timePickerEndValue != "请选择") {
          var maxDate_ = this.timePickerEndValue;
          maxDate_ = maxDate_.replace(/-/g, "/");
          var date = new Date(maxDate_);
          this.maxDateStart = date;
        }

        this.datetimePickerType = "start";
        this.mobileDatePickerShow = !this.mobileDatePickerShow;
      }
    },
    //选择结束日期
    handleChooseDateEndMobile: function handleChooseDateEndMobile() {
      if (!this.mobileDatePickerShow) {
        if (this.timePickerStartValue != "请选择") {
          var maxDate_ = this.timePickerStartValue;
          maxDate_ = maxDate_.replace(/-/g, "/");
          var date = new Date(maxDate_);
          this.minDateEnd = date;
        }

        this.datetimePickerType = "end";
        this.mobileDatePickerShow = !this.mobileDatePickerShow;
      }
    },
    //选择小程序
    handleChooseMiniProgramMobile: function handleChooseMiniProgramMobile() {
      if (!this.mobileChannelPickerShow) {
        this.mobileMiniProgramPickerShow = true;
      }
    },
    //确认选择小程序
    MiniProgramPickerConfirm: function MiniProgramPickerConfirm(res) {
      if (this.mobileMiniProgramPickerShow) {
        this.miniProgramMobileValue = res;
        this.mobileMiniProgramPickerShow = false;
      }
    },
    datetimePickerChange: function datetimePickerChange(e) {},
    //确认选择日期
    datetimePickerConfirm: function datetimePickerConfirm(res) {
      if (this.mobileDatePickerShow) {
        if (this.datetimePickerType == "start") {
          this.timePickerStartValue = res.toLocaleDateString();
        } else {
          this.timePickerEndValue = res.toLocaleDateString();
        }

        this.mobileDatePickerShow = !this.mobileDatePickerShow;
      }
    },
    //取消选择日期
    datetimePickerCancel: function datetimePickerCancel() {
      this.mobileDatePickerShow = !this.mobileDatePickerShow;
    },
    //开始搜索
    handleMobileSearch: function handleMobileSearch() {
      this.mobileSearchButtonLoading = true;
      this.getMobileList();
      this.mobileSearchButtonLoading = false;
      this.mobileSearchShow = false;
    },
    //限制页面跳转输入框只能输入数字
    jumpPageInput: function jumpPageInput() {
      this.pageJumpIndex = this.pageJumpIndex.replace(/[^\d]/g, "");
    },
    //跳转指定页面
    handleJumpPage: function handleJumpPage() {
      var jumpPage = parseInt(this.pageJumpIndex);
      if (jumpPage == this.mobileCurrentPage) return;

      if (jumpPage > Math.ceil(this.listTotal / this.pagesize)) {
        jumpPage = Math.ceil(this.listTotal / this.pagesize);
      }

      if (jumpPage < 1) {
        jumpPage = 1;
      }

      this.pageJumpIndex = jumpPage;
      this.mobileCurrentPage = jumpPage;
      this.getMobileList();
    }
  }
};
