import LineChart from "./components/LineChart";
import Vue from "vue";
import { getOrderStatus, getOrderCount, getEchartData } from "@/api/homepage";
import { setStore, getStore, removeStore } from "@/utils/store";
import { getSalesmanList, getChannelList, getProductList, getColorList } from "@/api/orderList";
Vue.use(_Cell);
Vue.use(_CellGroup);
Vue.use(_Button);
Vue.use(_Popup);
export default {
  components: {
    LineChart: LineChart
  },
  data: function data() {
    return {
      url: "https://lhtpic01.oss-cn-shenzhen.aliyuncs.com/common/logo-large.jpg",
      mobileUrl: "https://lhtpic01.oss-cn-shenzhen.aliyuncs.com/common/logo-small.png",
      overtimeTableData: [],
      orderTableData: [],
      headerShow: true,
      device: "",
      salemanOptions: [],
      channelOptions: [],
      productOptions: [],
      colorOptions: [],
      timeoutShow: false,
      orderShow: false
    };
  },
  created: function created() {
    this.handleGetData();
    this.getSalesman();
    this.getChannel();
    this.getProduct();
    this.getColor();
    this.device = this.$store.state.app.device;
  },
  computed: {
    deviceVal: function deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal: function deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    handleGetData: function handleGetData() {
      var _this = this;

      // 获取超时单信息
      getOrderStatus().then(function (res) {
        if (!res || !res.data) return;
        var statusdataList = [];
        var orderStatusList = res.data.split("<br>");
        orderStatusList.forEach(function (orderStatusItem) {
          var dataArr = orderStatusItem.split(":");
          var data = {
            category: dataArr[0],
            status: "有超时单"
          };
          statusdataList.push(data);
        });
        _this.overtimeTableData = statusdataList;
      }).catch(function (error) {}); // 获取订单信息

      getOrderCount().then(function (res) {
        if (!res || !res.data) return;
        var countdataList = [];
        var orderCountList = res.data.split("<br>");
        orderCountList.forEach(function (orderCountItem) {
          var dataArr = orderCountItem.split(":");
          var data = {
            category: dataArr[0],
            status: "有订单"
          };
          countdataList.push(data);
        });
        _this.orderTableData = countdataList;
      });
    },
    // 获取业务员列表
    getSalesman: function getSalesman() {
      var _this2 = this;

      getSalesmanList().then(function (res) {
        var salesmanList = res.data;
        salesmanList.forEach(function (salesmanItem) {
          var salesmanObject = {
            value: salesmanItem.id,
            label: salesmanItem.name
          };

          _this2.salemanOptions.push(salesmanObject);
        });
        setStore({
          name: "salesmanList",
          content: JSON.stringify(_this2.salemanOptions)
        });
      });
    },
    // 获取渠道列表
    getChannel: function getChannel() {
      var _this3 = this;

      getChannelList().then(function (res) {
        var channelList = res.data;
        channelList.forEach(function (channelItem) {
          var channelObject = {
            value: channelItem.id,
            label: channelItem.name
          };

          _this3.channelOptions.push(channelObject);
        });
        setStore({
          name: "channelList",
          content: JSON.stringify(_this3.channelOptions)
        });
      });
    },
    // 获取产品列表
    getProduct: function getProduct() {
      var _this4 = this;

      getProductList().then(function (res) {
        var productList = res.data;
        productList.forEach(function (productItem) {
          var productObject = {
            value: productItem.id,
            label: productItem.name
          };

          _this4.productOptions.push(productObject);
        });
        setStore({
          name: "productList",
          content: JSON.stringify(_this4.productOptions)
        });
      });
    },
    // 获取颜色列表
    getColor: function getColor() {
      var _this5 = this;

      if (!getStore({
        name: "colorList"
      })) {
        getColorList().then(function (res) {
          var colorList = res.data;
          colorList.forEach(function (colorItem) {
            if (colorItem.name) {
              var colorProject = {
                value: colorItem.id,
                label: colorItem.name
              };

              _this5.colorOptions.push(colorProject);
            }
          });
          setStore({
            name: "colorList",
            content: JSON.stringify(_this5.colorOptions)
          });
        });
      }
    },
    handleGetTimeOut: function handleGetTimeOut() {
      this.timeoutShow = true;
    },
    handleGetOrder: function handleGetOrder() {
      this.orderShow = true;
    }
  }
};
