    },
    // 取消导入京东
    mobileExportJDCancel: function mobileExportJDCancel() {},
    // 业务员快捷搜索
    handleSearchSalesmanItem: function handleSearchSalesmanItem() {
      this.salesPickerCurrentSelect = this.salesmanColumns.indexOf(this.salesmanSearchValue);
