import { getLoginLog, getSalesmanList, editLoginIp } from "@/api/orderList";
import permission from "@/directive/permission/index.js"; // 权限判断指令

import Vue from "vue";
Vue.use(_Pagination);
Vue.use(_Button);
Vue.use(_Popup);
Vue.use(_NavBar);
Vue.use(_Cell);
Vue.use(_CellGroup);
Vue.use(_DatetimePicker);
Vue.use(_Picker);
Vue.use(_Field);
Vue.use(_Icon);
Vue.use(_Toast);
Vue.use(_ActionSheet);
Vue.use(_Search);
export default {
  name: "system-loginLog",
  directives: {
    permission: permission
  },
  data: function data() {
    return {
      list: [],
      pickerOptions: {
        shortcuts: [{
          text: "上月",
          onClick: function onClick(picker) {
            var start = new Date(new Date().getFullYear(), new Date().getMonth() - 1, 1);
            var end = new Date(new Date().getFullYear(), new Date().getMonth() - 1, new Date(new Date().getFullYear(), new Date().getMonth(), 0).getDate());
            picker.$emit("pick", [start, end]);
          }
        }]
      },
      formLabelWidth: "120px",
      dialogTableVisible: false,
      currentPage: 1,
      //当前页
      mobileCurrentPage: 1,
      pagesizes: [20, 40, 60, 80, 100],
      //单页最大显示条数
      pagesize: 20,
      //单页内条数
      listTotal: 0,
      //总数
      device: "",
      mobileSearchShow: false,
      mobileSearchButtonLoading: false,
      timeSelectValue: "",
      tableMaxHeight: 0,
      salemanOptions: [],
      salesmanColumns: [],
      salemanValue: "",
      ipValue: "",
      salemanWidth: "",
      listLoading: false,
      pageJumpIndex: 1,
      searchButtonLoading: false,
      timePickerStartValue: "请选择",
      timePickerEndValue: "请选择",
      salesmanMobileValue: "请选择",
      ipaddressMobileValue: "",
      mobileDatePickerShow: false,
      mobileSalesmanPickerShow: false,
      datetimePickerType: "",
      salesmanSearchValue: "",
      salesPickerCurrentSelect: 0,
      minDate: new Date(1950, 10, 1),
      maxDate: new Date(),
      currentDate: new Date(),
      minDateEnd: new Date(),
      maxDateStart: new Date()
    };
  },
  created: function created() {
    this.getList();
    this.device = this.$store.state.app.device;
    window.addEventListener("resize", this.getHeight);
    this.getSalesman();
    this.getHeight();
  },
  destroyed: function destroyed() {
    window.removeEventListener("resize", this.getHeight);
  },
  computed: {
    deviceVal: function deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal: function deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    //获取数据
    getList: function getList() {
      var _this = this;

      this.listLoading = true;
      var searchList = [];

      if (this.timeSelectValue == null) {
        this.timeSelectValue = ["", ""];
      } else {
        this.timeSelectValue == "" ? this.timeSelectValue : ["", ""];
      }

      var paramsObj = {
        contains: false,
        page: this.currentPage,
        rows: this.pagesize
      };
      this.timeSelectValue[0] ? paramsObj.createTime = this.timeSelectValue[0] : "";
      this.timeSelectValue[1] ? paramsObj.createTimeEnd = this.timeSelectValue[1] : "";
      this.ipValue ? paramsObj.ip = this.ipValue : "";

      if (this.salemanValue.length > 0) {
        paramsObj.uids = this.salemanValue.join(",");
      }

      getLoginLog(paramsObj).then(function (res) {
        _this.listTotal = res.data.total;
        var tableData = res.data.rows;
        tableData.forEach(function (tableItem) {
          var createTime = tableItem.createTime,
              ip = tableItem.ip,
              province = tableItem.province,
              username = tableItem.username,
              fettle = tableItem.fettle;
          var orderItem = {
            username: username,
            ip: ip,
            province: province,
            createTime: createTime,
            fettle: fettle
          };
          searchList.push(orderItem);
        });
      });
      this.list = searchList;
      setTimeout(function () {
        _this.listLoading = false;
      }, 1000);
    },
    // 获取业务员列表
    getSalesman: function getSalesman() {
      var _this2 = this;

      getSalesmanList().then(function (res) {
        var salesmanList = res.data;
        salesmanList.forEach(function (salesmanItem) {
          var salesmanObject = {
            value: salesmanItem.id,
            label: salesmanItem.name
          };

          _this2.salemanOptions.push(salesmanObject);

          _this2.salesmanColumns.push(salesmanItem.label);
        });
      });
    },
    // 表格高度自适应
    getHeight: function getHeight() {
      var _this3 = this;

      this.$nextTick(function () {
        if (_this3.device === "desktop") {
          _this3.tableMaxHeight = document.body.offsetHeight - (200 + _this3.$refs.filterBox.offsetHeight + 40 + 18);
        } else {
          _this3.tableMaxHeight = document.body.offsetHeight - (100 + 40 + 40 + 88 + 10);
        }
      });
    },
    // 单击复制
    handleUseful: function handleUseful(row, column, cell, event) {
      var _this4 = this;

      if (this.device == "mobile") return;

      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }

      this.clickFlag = setTimeout(function () {
        var count = 0;
        if (column.label == undefined) return;

        if (column.label == "操作") {} else {
          var copyText = event.target.innerText;

          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");

            _this4.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            _this4.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    //表格页面总数改变
    handleSizeChange: function handleSizeChange(val) {
      this.pagesize = val;
      this.getList();
    },
    //选择表格当前页数
    handleCurrentChange: function handleCurrentChange(val) {
      this.currentPage = val;
      this.getList();
    },
    //搜索
    handleSearch: function handleSearch() {
      this.searchButtonLoading = true;
      this.getList();
      this.searchButtonLoading = false;
    },
    // 业务员选择器宽度自适应
    salemanChange: function salemanChange() {
      var inputWidth = 178; //选择器原始宽度 178px

      var optionWidth = this.salemanValue.length * 65 + 97; //一个标签宽度65px 右边预留97px

      this.salemanWidth = optionWidth > inputWidth ? "width:" + optionWidth + "px" : "width:" + inputWidth + "px";
    },

    /* 移动端事件 */
    // 获取数据
    getMobileList: function getMobileList() {
      var _this5 = this;

      this.listLoading = true;
      var searchList = [];
      var uids = [];
      var paramsObj = {
        contains: false,
        page: this.currentPage,
        rows: this.pagesize
      };

      if (this.salesmanMobileValue != "请选择") {
        var salesmanArr;

        if (this.salesmanMobileValue.length > 1) {
          salesmanArr = this.salesmanMobileValue.split(",");
          salesmanArr.forEach(function (salesmanItem) {
            _this5.salemanOptions.forEach(function (optionItem) {
              if (salesmanItem == optionItem.label) {
                uids.push(optionItem.value);
              }
            });
          });
        } else {
          this.salemanOptions.forEach(function (optionItem) {
            if (optionItem.label == _this5.salesmanMobileValue) {
              uids.push(optionItem.value);
            }
          });
        }
      }

      var timeStartValue = "";
      var timeEndValue = "";
      this.timePickerStartValue == "请选择" ? timeStartValue = "" : timeStartValue = this.timePickerStartValue.replace(/\//g, "-");
      this.timePickerEndValue == "请选择" ? timeEndValue = "" : timeEndValue = this.timePickerEndValue.replace(/\//g, "-");
      timeStartValue ? paramsObj.createTime = timeStartValue : "";
      timeEndValue ? paramsObj.createTimeEnd = timeEndValue : "";
      this.ipaddressMobileValue != "" ? paramsObj.ip = this.ipaddressMobileValue : "";

      if (uids.length > 0) {
        paramsObj.uids = uids.join(",");
      }

      getLoginLog(paramsObj).then(function (res) {
        _this5.listTotal = res.data.total;
        var tableData = res.data.rows;
        tableData.forEach(function (tableItem) {
          var createTime = tableItem.createTime,
              ip = tableItem.ip,
              province = tableItem.province,
              username = tableItem.username,
              fettle = tableItem.fettle;
          var orderItem = {
            username: username,
            ip: ip,
            province: province,
            createTime: createTime,
            fettle: fettle
          };
          searchList.push(orderItem);
        });
      });
      this.list = searchList;
      setTimeout(function () {
        _this5.listLoading = false;
      }, 1000);
    },
    // 选择开始日期
    handleChooseDateStartMobile: function handleChooseDateStartMobile() {
      if (!this.mobileDatePickerShow) {
        if (this.timePickerEndValue != "请选择") {
          var maxDate_ = this.timePickerEndValue;
          maxDate_ = maxDate_.replace(/-/g, "/");
          var date = new Date(maxDate_);
          this.maxDateStart = date;
        }

        this.datetimePickerType = "start";
        this.mobileDatePickerShow = !this.mobileDatePickerShow;
      }
    },
    // 选择结束日期
    handleChooseDateEndMobile: function handleChooseDateEndMobile() {
      if (!this.mobileDatePickerShow) {
        if (this.timePickerStartValue != "请选择") {
          var maxDate_ = this.timePickerStartValue;
          maxDate_ = maxDate_.replace(/-/g, "/");
          var date = new Date(maxDate_);
          this.minDateEnd = date;
        }

        this.datetimePickerType = "end";
        this.mobileDatePickerShow = !this.mobileDatePickerShow;
      }
    },
    datetimePickerChange: function datetimePickerChange(e) {},
    // 确认选择日期
    datetimePickerConfirm: function datetimePickerConfirm(res) {
      if (this.mobileDatePickerShow) {
        if (this.datetimePickerType == "start") {
          this.timePickerStartValue = res.toLocaleDateString();
        } else {
          this.timePickerEndValue = res.toLocaleDateString();
        }

        this.mobileDatePickerShow = !this.mobileDatePickerShow;
      }
    },
    // 取消选择日期
    datetimePickerCancel: function datetimePickerCancel() {
      this.mobileDatePickerShow = !this.mobileDatePickerShow;
    },
    // 选择业务员
    handleChooseSalesman: function handleChooseSalesman() {
      if (!this.mobileSalesmanPickerShow) {
        this.mobileSalesmanPickerShow = !this.mobileSalesmanPickerShow;
      }
    },
    // 确认选择业务员
    salesmanPickerConfirm: function salesmanPickerConfirm(res) {
      if (this.mobileSalesmanPickerShow) {
        if (this.salesmanMobileValue == "请选择") {
          this.salesmanMobileValue = res;
        } else {
          if (this.salesmanMobileValue.indexOf(",") != -1) {
            var salesmanArr = new Array();
            salesmanArr = this.salesmanMobileValue.split(",");

            if (salesmanArr.indexOf(res) == -1) {
              this.salesmanMobileValue = this.salesmanMobileValue + "," + res;
            } else {
              _Toast("请勿重复选择");
            }
          } else {
            if (this.salesmanMobileValue != res) {
              this.salesmanMobileValue = this.salesmanMobileValue + "," + res;
            } else {
              _Toast("请勿重复选择");
            }
          }
        }

        this.mobileSalesmanPickerShow = !this.mobileSalesmanPickerShow;
      }
    },
    // 业务员快捷搜索
    handleSearchSalesmanItem: function handleSearchSalesmanItem() {
      this.salesPickerCurrentSelect = this.salesmanColumns.indexOf(this.salesmanSearchValue);
    },
    //搜索
    handleSearchMobile: function handleSearchMobile() {
      this.mobileSearchShow = !this.mobileSearchShow;
    },
    //取消搜索
    handleSearchMobileCancel: function handleSearchMobileCancel() {
      this.mobileSearchShow = !this.mobileSearchShow;
    },
    //确认搜索
    handleMobileSearch: function handleMobileSearch() {
      this.getMobileList();
      this.mobileSearchShow = false;
    },
    //清空所选
    handleSearchMobileClearAll: function handleSearchMobileClearAll() {
      this.timePickerStartValue == "请选择" ? "" : this.timePickerStartValue = "请选择", this.timePickerEndValue == "请选择" ? "" : this.timePickerEndValue = "请选择", this.salesmanMobileValue == "请选择" ? "" : this.salesmanMobileValue = "请选择", this.ipaddressMobileValue == "" ? "" : this.ipaddressMobileValue = "";
    },
    //分页器改变
    handlePageChange: function handlePageChange() {},
    // 限制页面跳转输入框只能输入数字
    jumpPageInput: function jumpPageInput() {
      this.pageJumpIndex = this.pageJumpIndex.replace(/[^\d]/g, "");
    },
    // 跳转指定页面
    handleJumpPage: function handleJumpPage() {
      var jumpPage = parseInt(this.pageJumpIndex);
      if (jumpPage == this.mobileCurrentPage) return;

      if (jumpPage > Math.ceil(this.listTotal / this.pagesize)) {
        jumpPage = Math.ceil(this.listTotal / this.pagesize);
      }

      if (jumpPage < 1) {
        jumpPage = 1;
      }

      this.pageJumpIndex = jumpPage;
      this.mobileCurrentPage = jumpPage;
      this.getMobileList();
    }
  }
};
