import tencentTable from "@/components/tencentTable";
import {
  getTencentOrderList,
  editTencentOrder,
  editUsefulTencent
} from "@/api/orderList";

export default {
  components: {
    tencentTable
  },
  data() {
    return {
      list: [],
      text: "temporary",
      paramsStorage: {},
      searchButtonFlag: false,
      searchButtonMobileFlag: false,
      mobileSearchShowFlag: false
    };
  },
  created() {
    this.list = this.getOrderList();
  },
  methods: {
    // 获取列表
    getOrderList() {
      let orderList = [];
      const contains = false;
      const rows = 1000;
      const page = 1;
      const text = this.text;
      getTencentOrderList({
        contains: contains,
        rows: rows,
        page: page,
        text: text
      }).then(res => {
        const tableData = res.data.rows;
        tableData.forEach(tableItem => {
          const {
            id,
            cpName,
            pid,
            productName,
            colorName,
            username,
            telephone,
            totalCost,
            pageInfo,
            pNum,
            num,
            price,
            size,
            isRepeat,
            address,
            createTime,
            remark,
            mode,
            isImport,
            name,
            uid,
            operator,
            operatingTime
          } = tableItem;
          const orderItem = {
            id: id,
            channel: cpName,
            pid: pid,
            productName: productName,
            color: colorName,
            name: name,
            phoneNumber: telephone,
            count: num,
            price: totalCost,
            packageAttributes: pageInfo,
            size: size,
            repeatOrder: isRepeat,
            address: address,
            createTime: createTime,
            remarks: remark,
            isUseful: mode,
            logisticsState: isImport,
            salesman: username,
            uid: uid,
            operator: operator,
            nuclearOrderInterval: operatingTime
          };
          orderList.push(orderItem);
        });
      });
      return orderList;
    },
    // 编辑
    editConfirm(data) {
      const text = this.text;
      const {
        id,
        productId,
        name,
        pageInfo,
        count,
        price,
        remarks,
        address
      } = data;
      const params = {
        id: id,
        text: text,
        productId: productId,
        name: name,
        pageInfo: pageInfo,
        num: count,
        totalCost: price,
        remark: remarks,
        address: address
      };
      editTencentOrder(params)
        .then(res => {
          this.reloadPage();
          setTimeout(() => {
            this.$message({
              message: "修改成功",
              type: "success"
            });
          }, 1000);
        })
        .catch(error => {
          this.$message.error("修改失败");
          console.log(error);
        });
    },
    // 搜索
    clickToSearch(data) {
      let searchList = [];
      const contains = false;
      const rows = 1000;
      const page = 1;
      const text = this.text;
      const {
        createTime,
        createTimeEnd,
        cid,
        uid,
        id,
        idEnd,
        isRepeat,
        mode,
        name,
        productId,
        telephone,
        totalCost,
        totalCostEnd,
        packageAttributes
      } = data;
      let paramsObj = {
        contains: contains,
        rows: rows,
        page: page,
        text: text
      };
      createTime ? (paramsObj.createTime = createTime) : "";
      createTimeEnd ? (paramsObj.createTimeEnd = createTimeEnd) : "";
      cid ? (paramsObj.cid = cid) : "";
      id ? (paramsObj.id = id) : "";
      idEnd ? (paramsObj.idEnd = idEnd) : "";
      isRepeat ? (paramsObj.isRepeat = isRepeat) : "";
      mode ? (paramsObj.mode = mode) : "";
      name ? (paramsObj.name = name) : "";
      productId ? (paramsObj.productId = productId) : "";
      telephone ? (paramsObj.telephone = telephone) : "";
      totalCost ? (paramsObj.totalCost = totalCost) : "";
      totalCostEnd ? (paramsObj.totalCostEnd = totalCostEnd) : "";
      packageAttributes
        ? (paramsObj.packageAttributes = packageAttributes)
        : "";
      if (uid.length > 0) {
        let uidStr = uid.join(",");
        paramsObj.uids = uidStr;
      }
      this.paramsStorage = paramsObj;
      getTencentOrderList(paramsObj)
        .then(res => {
          const tableData = res.data.rows;
          tableData.forEach(tableItem => {
            const {
              id,
              cpName,
              pid,
              productName,
              colorName,
              username,
              telephone,
              totalCost,
              pageInfo,
              pNum,
              num,
              price,
              size,
              isRepeat,
              address,
              createTime,
              remark,
              mode,
              isImport,
              name,
              uid,
              operator,
              operatingTime
            } = tableItem;
            const orderItem = {
              id: id,
              channel: cpName,
              pid: pid,
              productName: productName,
              color: colorName,
              name: name,
              phoneNumber: telephone,
              count: num,
              price: totalCost,
              packageAttributes: pageInfo,
              size: size,
              repeatOrder: isRepeat,
              address: address,
              createTime: createTime,
              remarks: remark,
              isUseful: mode,
              logisticsState: isImport,
              salesman: username,
              uid: uid,
              operator: operator,
              nuclearOrderInterval: operatingTime
            };
            searchList.push(orderItem);
          });
          this.list = searchList;
        })
        .catch(error => {
          console.log(error);
        });
    },
    clickToMobileSearchPage() {
      this.mobileSearchShowFlag = true;
    },
    clickToCloseMobileSearchPage() {
      this.mobileSearchShowFlag = false;
    },
    // 移动端搜索
    clickToMobileSearch(data) {
      this.searchButtonMobileFlag = true;
      let searchList = [];
      const text = this.text;
      const contains = false;
      const rows = 1000;
      const page = 1;
      const {
        createTime,
        createTimeEnd,
        cid,
        uids,
        id,
        idEnd,
        isRepeat,
        mode,
        name,
        productId,
        telephone,
        totalCost,
        totalCostEnd,
        pageInfo
      } = data;
      let paramsObj = {
        contains: contains,
        rows: rows,
        page: page,
        text: text
      };
      createTime ? (paramsObj.createTime = createTime) : "";
      createTimeEnd ? (paramsObj.createTimeEnd = createTimeEnd) : "";
      cid ? (paramsObj.cid = cid) : "";
      id ? (paramsObj.id = id) : "";
      idEnd ? (paramsObj.idEnd = idEnd) : "";
      isRepeat ? (paramsObj.isRepeat = isRepeat) : "";
      mode ? (paramsObj.mode = mode) : "";
      name ? (paramsObj.name = name) : "";
      productId ? paramsObj.productId : "";
      telephone ? (paramsObj.telephone = telephone) : "";
      totalCost ? (paramsObj.totalCost = totalCost) : "";
      totalCostEnd ? (paramsObj.totalCostEnd = totalCostEnd) : "";
      pageInfo ? (paramsObj.pageInfo = pageInfo) : "";
      if (uids.length > 0) {
        let uidStr = uids.join(",");
        paramsObj.uids = uidStr;
      }
      this.paramsStorage = paramsObj;
      getTencentOrderList(paramsObj)
        .then(res => {
          const tableData = res.data.rows;
          tableData.forEach(tableItem => {
            const {
              id,
              cpName,
              pid,
              productName,
              colorName,
              username,
              telephone,
              totalCost,
              pageInfo,
              pNum,
              num,
              price,
              size,
              isRepeat,
              address,
              createTime,
              remark,
              mode,
              isImport,
              name,
              uid,
              operator,
              operatingTime
            } = tableItem;
            const orderItem = {
              id: id,
              channel: cpName,
              pid: pid,
              productName: productName,
              color: colorName,
              name: name,
              phoneNumber: telephone,
              count: num,
              price: totalCost,
              packageAttributes: pageInfo,
              size: size,
              repeatOrder: isRepeat,
              address: address,
              createTime: createTime,
              remarks: remark,
              isUseful: mode,
              logisticsState: isImport,
              salesman: username,
              uid: uid,
              operator: operator,
              nuclearOrderInterval: operatingTime
            };
            searchList.push(orderItem);
          });
          this.list = searchList;
          this.searchButtonMobileFlag = false;
          this.mobileSearchShowFlag = false;
        })
        .catch(error => {
          this.searchButtonMobileFlag = false;
          this.mobileSearchShowFlag = false;
          console.log(error);
        });
    },
    //修改可用状态
    clickToChangeUseful(data) {
      const text = this.text;
      const { id, mode } = data;
      let modeInt;
      mode == 1 ? (modeInt = 0) : (modeInt = 1);
      editUsefulTencent({
        id: id,
        text: text,
        mode: modeInt
      })
        .then(res => {
          this.reloadPage();
          setTimeout(() => {
            this.$message({
              message: "修改成功",
              type: "success"
            });
          }, 1000);
        })
        .catch(error => {
          this.$message.error("修改失败");
          console.log(error);
        });
    },
    // 重载页面
    reloadPage() {
      if (this.paramsStorage === {}) {
        this.list = this.getOrderList();
      } else {
        let searchList = [];
        getTencentOrderList(this.paramsStorage).then(res => {
          const tableData = res.data.rows;
          tableData.forEach(tableItem => {
            const {
              id,
              cpName,
              pid,
              productName,
              colorName,
              username,
              telephone,
              totalCost,
              pageInfo,
              pNum,
              num,
              price,
              size,
              isRepeat,
              address,
              createTime,
              remark,
              mode,
              isImport,
              name,
              uid,
              operator,
              operatingTime
            } = tableItem;
            const orderItem = {
              id: id,
              channel: cpName,
              pid: pid,
              productName: productName,
              color: colorName,
              name: name,
              phoneNumber: telephone,
              count: num,
              price: totalCost,
              packageAttributes: pageInfo,
              size: size,
              repeatOrder: isRepeat,
              address: address,
              createTime: createTime,
              remarks: remark,
              isUseful: mode,
              logisticsState: isImport,
              salesman: username,
              uid: uid,
              operator: operator,
              nuclearOrderInterval: operatingTime
            };
            searchList.push(orderItem);
          });
          this.list = searchList;
        });
      }
    }
  }
};
