import Vue from "vue";
import { getStore } from "@/utils/store";
import permission from "@/directive/permission/index.js"; // 权限判断指令
import XEUtils from "xe-utils";
import { parseTime } from "@/utils";
import { setTimeout, clearTimeout } from "timers";
import {
  getTencentOrderList,
  getSalesmanList,
  exportTencentExcel,
  editTencentOrder,
  importJD,
  getProductSelectList,
  getChannelSelectList,
  replaceTencentDuplicates,
  editUsefulTencent,
  getTencentOrderInfo
} from "@/api/orderList";
import {
  Pagination,
  Button,
  Popup,
  NavBar,
  Cell,
  CellGroup,
  DatetimePicker,
  Picker,
  Field,
  Icon,
  Toast,
  ActionSheet,
  Search
} from "vant";

Vue.use(Pagination);
Vue.use(Button);
Vue.use(Popup);
Vue.use(NavBar);
Vue.use(Cell);
Vue.use(CellGroup);
Vue.use(DatetimePicker);
Vue.use(Picker);
Vue.use(Field);
Vue.use(Icon);
Vue.use(Toast);
Vue.use(ActionSheet);
Vue.use(Search);

export default {
  name: "tencentTable",
  props: ["category"],
  directives: { permission },
  data() {
    return {
      list: [],
      pickerOptions: {
        shortcuts: [
          {
            text: "上月",
            onClick(picker) {
              const start = new Date(
                new Date().getFullYear(),
                new Date().getMonth() - 1,
                1
              );
              const end = new Date(
                new Date().getFullYear(),
                new Date().getMonth() - 1,
                new Date(
                  new Date().getFullYear(),
                  new Date().getMonth(),
                  0
                ).getDate()
              );
              picker.$emit("pick", [start, end]);
            }
          }
        ]
      },
      salemanOptions: [],
      salesmanColumns: [],
      channelOptions: [],
      channelColumns: [],
      productOptions: [],
      productColumns: [],
      productAllOptions: [],
      usefulOptions: [
        {
          value: "0",
          label: "无效单"
        },
        {
          value: "1",
          label: "有效单"
        }
      ],
      usefulColumns: ["无效单", "有效单"],
      repeatOrderOptions: [
        {
          value: "0",
          label: "不重单"
        },
        {
          value: "1",
          label: "重单"
        }
      ],
      repeatOrderColumns: ["不重单", "重单"],
      repeatIpOptions: [
        {
          value: "0",
          label: "不重复ip"
        },
        {
          value: "1",
          label: "重复ip"
        }
      ],
      repeatIpColumns: ["不重复ip", "重复ip"],
      repeatNamePhoneOptions: [
        {
          value: "0",
          label: "不重复姓名电话"
        },
        {
          value: "1",
          label: "重复姓名电话"
        }
      ],
      repeatNamePhoneColumns: ["不重复", "重复"],
      exportjdOptions: [
        {
          value: "0",
          label: "未导入"
        },
        {
          value: "1",
          label: "已导入"
        }
      ],
      exportJDColumns: ["未导入", "已导入"],
      timeSelectValue: "",
      salemanValue: "",
      channelValue: "",
      productValue: "",
      packageAttributesValue: "",
      usefulValue: "",
      repeatOrderValue: "",
      repeatIpValue: "",
      repeatNamePhoneValue: "",
      exportjdValue: "",
      nameInput: "",
      phoneNumberInput: "",
      minIdInput: "",
      maxIdInput: "",
      minPriceInput: "",
      maxPriceInput: "",
      formLabelWidth: "120px",
      editDialogVisible: false,
      form: {
        productName: "",
        name: "",
        pageInfo: "",
        num: "",
        totalCost: "",
        remark: "",
        address: ""
      },
      currentEditID: 0,
      downloadLoading: false,
      listLoading: true,
      currentPage: 1, //当前页
      mobileCurrentPage: 1,
      pagesizes: [300, 500, 1000, 5000], //单页最大显示条数
      pagesize: 300, //单页内条数
      listTotal: 0, //总数
      salemanWidth: "",
      importTypeDialogVisible: false,
      multipleSelection: [],
      clickFlag: null, // 单击定时器
      mobileSearchShow: false,
      mobileDatePickerShow: false,
      mobileSalesmanPickerShow: false,
      mobileChannelPickerShow: false,
      mobileAccountPickerShow: false,
      mobileProductPickerShow: false,
      mobilePackageAttributesPickerShow: false,
      mobileUsefulPickerShow: false,
      mobileRepeatOrderPickerShow: false,
      mobileRepeatIpPickerShow: false,
      mobileRepeatNamePhonePickerShow: false,
      mobileExportJDPickerShow: false,
      minDate: new Date(1950, 10, 1),
      maxDate: new Date(),
      currentDate: new Date(),
      minDateEnd: new Date(),
      maxDateStart: new Date(),
      timePickerStartValue: "请选择",
      timePickerEndValue: "请选择",
      salesmanMobileValue: "请选择",
      channelMobileValue: "请选择",
      accountMobileValue: "请选择",
      productMobileValue: "请选择",
      packageAttributesMobileValue: "请选择",
      usefulMobileValue: "请选择",
      repeatOrderMobileValue: "请选择",
      repeatIpMobileValue: "请选择",
      repeatNamePhoneMobileValue: "请选择",
      exportJDMobileValue: "请选择",
      nameMobileValue: "",
      pageInfoMobileValue: "",
      phoneMobileValue: "",
      minIdMobileValue: "",
      maxIdMobileValue: "",
      minPriceMobileValue: "",
      maxPriceMobileValue: "",
      device: "",
      salesmanSearchValue: "",
      channelSearchValue: "",
      productSearchValue: "",
      accountSearchValue: "",
      PackageAttributesSearchValue: "",
      salesPickerCurrentSelect: 0,
      channelPickerCurrentSelect: 0,
      productPickerCurrentSelect: 0,
      accountPickerCurrentSelect: 0,
      packageAttributesPickerCurrentSelect: 0,
      datetimePickerType: "",
      mobileSearchButtonLoading: false,
      tableMaxHeight: 0,
      pageJumpIndex: 1,
      contains: false,
      productChangeLoading: false,
      channelChangeLoading: false,
      dialogFormVisible: false,
      replaceForm: {
        pageInfo: ""
      },
      cate: "",
      filterList: [],
      exportType: "1",
      transport: "1",
      orderType: "",
      orderName: ""
    };
  },
  created() {
    this.device = this.$store.state.app.device;
    window.addEventListener("resize", this.getHeight);
    this.$nextTick(() => {
      if (this.device === "desktop") {
        this.getList();
        this.getProductList();
        this.getChannelList();
      } else {
        this.getMobileList();
        this.getProductListMobile();
        this.getChannelListMobile();
      }
    });
    this.getHeight();
    this.getSalesman();
    this.getProduct();
    if (this.category === "temporary") {
      this.filterList = [
        { label: "手环", value: "igbracelet" },
        { label: "早教机", value: "trainer" },
        { label: "冷风机", value: "fan" },
        { label: "其它", value: "temporary" }
      ];
    }
  },
  destroyed() {
    window.removeEventListener("resize", this.getHeight);
  },
  computed: {
    deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    // 获取数据
    getList() {
      this.listLoading = true;
      if (this.timeSelectValue == null) {
        this.timeSelectValue = ["", ""];
      } else {
        this.timeSelectValue == "" ? this.timeSelectValue : ["", ""];
      }
      let paramsObj = {
        contains: this.contains,
        rows: this.pagesize,
        page: this.currentPage,
        text: this.category
      };
      this.timeSelectValue[0]
        ? (paramsObj.createTime = this.timeSelectValue[0])
        : "";
      this.timeSelectValue[1]
        ? (paramsObj.createTimeEnd = this.timeSelectValue[1])
        : "";
      this.channelValue ? (paramsObj.cid = this.channelValue) : "";
      this.minIdInput ? (paramsObj.id = this.minIdInput) : "";
      this.maxIdInput ? (paramsObj.idEnd = this.maxIdInput) : "";
      this.repeatOrderValue ? (paramsObj.isRepeat = this.repeatOrderValue) : "";
      this.repeatIpValue ? (paramsObj.isIpRepeat = this.repeatIpValue) : "";
      this.usefulValue ? (paramsObj.mode = this.usefulValue) : "";
      this.exportjdValue ? (paramsObj.isImport = this.exportjdValue) : "";
      this.repeatNamePhoneValue
        ? (paramsObj.isNameRepeat = this.repeatNamePhoneValue)
        : "";
      this.nameInput ? (paramsObj.name = this.nameInput) : "";
      this.productValue ? (paramsObj.productId = this.productValue) : "";
      this.phoneNumberInput
        ? (paramsObj.telephone = this.phoneNumberInput)
        : "";
      this.minPriceInput ? (paramsObj.totalCost = this.minPriceInput) : "";
      this.maxPriceInput ? (paramsObj.totalCostEnd = this.maxPriceInput) : "";
      this.packageAttributesValue
        ? (paramsObj.pageInfo = this.packageAttributesValue)
        : "";
      if (this.salemanValue.length > 0) {
        paramsObj.uids = this.salemanValue.join(",");
      }
      paramsObj.cate = this.cate;
      if (this.orderType != "") {
        paramsObj.order = this.orderType;
        paramsObj.sort = this.orderName;
      }
      getTencentOrderList(paramsObj)
        .then(res => {
          this.listTotal = res.data.total;
          this.list = res.data.rows;
          this.listLoading = false;
        })
        .catch(error => {
          this.$message.error("获取数据时出错");
          this.listLoading = false;
        });
    },
    // 更改背景色
    cellClassName({ row, rowIndex, column, columnIndex }) {
      if (column.property === "name") {
        if (row.repeatName == "1") {
          return "col-red";
        }
      }
      if (column.property === "telephone") {
        if (row.repeatTelephone == "1") {
          return "col-red";
        }
      }
    },
    // 筛选
    filterMethod({ column, field, values, filters }) {
      this.cate = values[0];
      this.getList();
    },
    // 获取业务员列表
    getSalesman() {
      let salesmanList = getStore({ name: "salesmanList" });
      this.salemanOptions = JSON.parse(salesmanList);
      let salesmanColumns = [];
      this.salemanOptions.forEach(salesmanItem => {
        salesmanColumns.push(salesmanItem.label);
      });
      this.salesmanColumns = salesmanColumns;
    },
    // 获取产品列表
    getProduct() {
      let productList = getStore({ name: "productList" });
      this.productAllOptions = JSON.parse(productList);
    },
    // 表格高度自适应
    getHeight() {
      this.$nextTick(() => {
        if (this.device === "desktop") {
          this.tableMaxHeight =
            document.body.offsetHeight -
            (200 + this.$refs.filterBox.offsetHeight + 40 +18);
        } else {
          this.tableMaxHeight =
            document.body.offsetHeight - (100 + 40 + 40 + 86 + 10 + 37);
        }
      });
    },
    // 单击复制
    handleUseful({ row, rowIndex, column, columnIndex }, event) {
      if (this.device == "mobile") return;
      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }
      this.clickFlag = setTimeout(() => {
        let count = 0;
        if (column.title == undefined) return;
        if (column.title == "是否可用") {
          this.list.forEach(item => {
            if (item.id == row.id) {
              this.handleEditUseful({
                id: row.id,
                mode: this.list[count].mode
              });
            } else {
              count++;
            }
          });
        } else {
          let copyText = event.target.innerText;
          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");
            this.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            this.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    // 修改可用状态
    handleEditUseful(data) {
      const { id, mode } = data;
      let modeInt;
      mode == 1 ? (modeInt = 0) : (modeInt = 1);
      editUsefulTencent({
        id: id,
        text: this.category,
        mode: modeInt
      })
        .then(res => {
          this.getList();
          setTimeout(() => {
            this.$message({
              message: "修改成功",
              type: "success"
            });
          }, 1000);
        })
        .catch(error => {
          this.$message.error("修改失败");
        });
    },
    // 双击编辑
    handleEdit({ row, rowIndex, column, columnIndex }, event) {
      if (this.device == "mobile") return;
      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }
      let timestamp = Date.parse(new Date())
      getTencentOrderInfo({'id':row.id,'text':this.category,"_":timestamp}).then(res => {
        console.log(res)
          this.form = res.data
          this.currentEditID = row.id;
          this.editDialogVisible = true;
      })
    },
    // 取消编辑
    handleEditCancel() {
      this.editDialogVisible = false;
    },
    // 确认编辑
    handleEditConfirm(row, column, event) {
      let count = 0;
      this.list.forEach(item => {
        if (item.id == this.currentEditID) {
          const params = {
            id: this.list[count].id,
            text: this.category,
            productId: this.form.productId,
            name: this.form.name,
            pageInfo: this.form.pageInfo,
            num: this.form.num,
            totalCost: this.form.totalCost,
            remark: this.form.remark,
            address: this.form.address
          };
          editTencentOrder(params)
            .then(res => {
              this.getList();
              setTimeout(() => {
                this.$message({
                  message: "修改成功",
                  type: "success"
                });
              }, 1000);
            })
            .catch(error => {
              this.$message.error("修改失败");
            });
        } else {
          count++;
        }
      });
      this.editDialogVisible = false;
    },
    // 搜索验证
    searchVerify() {
      if (
        (this.minIdInput && !this.maxIdInput) ||
        (!this.minIdInput && this.maxIdInput)
      ) {
        this.$message({
          message: "未填写完整的ID范围",
          type: "warning"
        });
        return false;
      }
      if (
        (this.minPriceInput && !this.maxPriceInput) ||
        (!this.minPriceInput && this.maxPriceInput)
      ) {
        this.$message({
          message: "未填写完整的价格范围",
          type: "warning"
        });
        return false;
      }
      return true;
    },
    // 搜索
    handleSearch() {
      if (!this.searchVerify()) return;
      this.searchButtonLoading = true;
      this.getList();
      this.searchButtonLoading = false;
    },
    // 清空搜索项
    handleClearSearch() {
      this.timeSelectValue = "";
      this.salemanValue = [];
      this.channelValue = "";
      this.productValue = "";
      this.nameInput = "";
      this.phoneNumberInput = "";
      this.usefulValue = "";
      this.repeatOrderValue = "";
      this.repeatIpValue = "";
      this.repeatNamePhoneValue = "";
      this.exportjdValue = "";
      this.minIdInput = "";
      this.maxIdInput = "";
      this.minPriceInput = "";
      this.maxPriceInput = "";
      this.packageAttributesValue = "";
      this.salemanChange();
    },
    // 选择表格尺寸
    handleSizeChange(val) {
      this.pagesize = val;
      this.getList();
    },
    // 选择表格当前页数
    handleCurrentChange(val) {
      this.currentPage = val;
      this.getList();
    },
    // 导出excel
    handleDownload() {
      this.downloadLoading = true;
      if (this.multipleSelection.length === 0) {
        this.$message.error("未选择任何数据");
        this.downloadLoading = false;
        return;
      }

      let ids = [];
      this.multipleSelection.forEach(selectItem => {
        ids.push(selectItem.id);
      });
      let idsStr = ids.join(",");
      exportTencentExcel(this.category, {
        ids: idsStr,
        logistics: "yd"
      }).then(res => {
        const blob = new Blob([res], {
          type: "application/vnd.mx-excel;charset=utf-8"
        });
        let myDate = new Date();
        let year = myDate.getFullYear();
        let month = myDate.getMonth() + 1;
        let day = myDate.getDate();
        var downloadElement = document.createElement("a");
        var href = window.URL.createObjectURL(blob); //创建下载的链接
        downloadElement.href = href;
        downloadElement.download = `订单 ${year}-${month}-${day}.xls`; //下载后文件名
        document.body.appendChild(downloadElement);
        downloadElement.click(); //点击下载
        document.body.removeChild(downloadElement); //下载完成移除元素
        window.URL.revokeObjectURL(href); //释放掉blob对象
      });
      this.downloadLoading = false;
    },
    // 业务员选择器宽度自适应
    salemanChange() {
      const inputWidth = 178; //选择器原始宽度 178px
