import Vue from "vue";
import XEUtils from "xe-utils";
import permission from "@/directive/permission/index.js"; // 权限判断指令
import { getMonitorProductList, importMonitorProduct } from "@/api/orderList";
import { parseTime } from "@/utils";
import { setTimeout, clearTimeout } from "timers";
import {
  Pagination,
  Button,
  Popup,
  NavBar,
  Cell,
  CellGroup,
  DatetimePicker,
  Picker,
  Field,
  Icon,
  Toast,
  ActionSheet,
  Search
} from "vant";

Vue.use(Pagination);
Vue.use(Button);
Vue.use(Popup);
Vue.use(NavBar);
Vue.use(Cell);
Vue.use(CellGroup);
Vue.use(DatetimePicker);
Vue.use(Picker);
Vue.use(Field);
Vue.use(Icon);
Vue.use(Toast);
Vue.use(ActionSheet);
Vue.use(Search);

export default {
  name: "system-monitorProduct",
  directives: { permission },
  data() {
    return {
      list: [],
      formLabelWidth: "120px",
      SearchButtonLoading: false,
      clearSearchButtonLoading: false,
      importProdcutLoading: false,
      listLoading: false,
      currentPage: 1, //当前页
      mobileCurrentPage: 1,
      pagesizes: [500, 1000, 2000], //单页最大显示条数
      pagesize: 500, //单页内条数
      listTotal: 0, //总数
      clickFlag: null, // 单击定时器
      mobileSearchShow: false,
      mobileSearchButtonLoading: false,
      tableMaxHeight: 0,
      pageJumpIndex: 1,
      nameInput: "",
      nameMobileValue: "",
      dialogVisible: false,
      urlInput: "",
      fileList: [],
      importLoading: false,
      baseUrl: ""
    };
  },
  created() {
    this.getList();
    this.device = this.$store.state.app.device;
    window.addEventListener("resize", this.getHeight);
    this.getHeight();
    this.baseUrl = process.env.VUE_APP_BASE_API;
  },
  destroyed() {
    window.removeEventListener("resize", this.getHeight);
  },
  computed: {
    deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    // 获取数据
    getList() {
      this.listLoading = true;
      let searchList = [];
      let paramsObj = {
        contains: false,
        page: this.currentPage,
        rows: this.pagesize
      };
      this.nameInput == "" ? (paramsObj.name = this.nameInput) : "";
      getMonitorProductList(paramsObj).then(res => {
        this.listTotal = res.data.total;
        const tableList = res.data.rows;
        tableList.forEach(tableItem => {
          const {
            createTime,
            img,
            name,
            numbering,
            oneUp,
            threeUp,
            sixUp,
            twelveUp,
            twentyfourUp,
            twentyfourPercentage,
            sellNum,
            shopId,
            shopName,
            skuMinPrice,
            status,
            text,
            updateTime,
            url
          } = tableItem;
          const orderItem = {
            productName: name,
            storeName: shopName,
            productMainImageUrl: img,
            minPrice: skuMinPrice,
            createTime: createTime,
            updateTime: updateTime,
            count: sellNum,
            withInOneHour: oneUp,
            withInThreeHour: threeUp,
            withInSixHour: sixUp,
            withInTwelveHour: twelveUp,
            yestoday: twentyfourUp,
            growthRate: twentyfourPercentage,
            linkAddress: text,
            linkChannel: url
          };
          searchList.push(orderItem);
        });
        this.list = searchList;
      });
      setTimeout(() => {
        this.listLoading = false;
      }, 1000);
    },
    //表格高度自适应
    getHeight() {
      let otherHeight = this.device == "desktop" ? 250 : 200;
      this.tableMaxHeight = window.innerHeight - otherHeight;
    },
    //单击复制
    handleUseful(row, column, cell, event) {
      if (this.device == "mobile") return;
      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }
      this.clickFlag = setTimeout(() => {
        let count = 0;
        if (column.label == undefined) return;
        if (column.label == "链接地址") {
          window.open(row.linkChannel);
        } else {
          let copyText = event.target.innerText;
          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");
            this.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            this.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    //搜索
    handleSearch() {
      if (this.nameInput == "") {
        this.$message.error("请输入产品名称");
        return;
      }
      this.SearchButtonLoading = true;
      this.getList();
      this.SearchButtonLoading = false;
    },
    handleClearSearch() {},
    //选择表格尺寸
    handleSizeChange(val) {
      this.pagesize = val;
      this.getList();
    },
    //选择表格当前页数
    handleCurrentChange(val) {
      this.currentPage = val;
      this.getList();
    },
    //导入产品
    handleImportProduct() {
      this.dialogVisible = true;
    },
    // 确认选择
    submitUpload() {
      this.$refs.upload.submit();
      this.dialogVisible = false;
    },
    // 选择文件
    handlePreview(file) {
      console.log(file);
    },
    // 移除选择
    handleRemove(file, fileList) {
      console.log(file, fileList);
    },
    // 关闭
    handleClose() {
      this.dialogVisible = false;
    },
    // 导入商品
    handleImportProductByUrl() {
      if (this.urlInput == "") {
        this.$message.error("请输入URL地址");
        return;
      }
      this.importLoading = true;
      importMonitorProduct({ url: this.urlInput }).then(res => {
        if (res.status == 200) {
          this.dialogVisible = false;
          this.importLoading = false;
          this.$alert(res.data.replace(/;/g, "<br>"), "导入结果", {
            dangerouslyUseHTMLString: true,
            confirmButtonText: "确定",
            callback: action => {}
          });
        }
      });
    },

    /* 移动端事件 */

    //获取数据
    getMobileList() {
      this.listLoading = true;
      let searchList = [];
      let paramsObj = {
        contains: false,
        page: this.mobileCurrentPage,
        rows: this.pagesize
      };
      this.nameMobileValue != "" ? (paramsObj.name = this.nameMobileValue) : "";
      getMonitorProductList(paramsObj).then(res => {
        this.listTotal = res.data.total;
        const tableList = res.data.rows;
        tableList.forEach(tableItem => {
          const {
            createTime,
            img,
            name,
            numbering,
            oneUp,
            threeUp,
            sixUp,
            twelveUp,
            twentyfourUp,
            twentyfourPercentage,
            sellNum,
            shopId,
            shopName,
            skuMinPrice,
            status,
            text,
            updateTime,
            url
          } = tableItem;
          const orderItem = {
            productName: name,
            storeName: shopName,
            productMainImageUrl: img,
            minPrice: skuMinPrice,
            createTime: createTime,
            updateTime: updateTime,
            count: sellNum,
            withInOneHour: oneUp,
            withInThreeHour: threeUp,
            withInSixHour: sixUp,
            withInTwelveHour: twelveUp,
            yestoday: twentyfourUp,
            growthRate: twentyfourPercentage,
            linkAddress: text,
            linkChannel: url
          };
          searchList.push(orderItem);
        });
        this.list = searchList;
      });
      setTimeout(() => {
        this.listLoading = false;
      }, 1000);
    },
    //分页器
    handlePageChange() {
      this.getMobileList();
    },
    //点击搜索
    handleSearchMobile() {
      if (!this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    //取消搜索
    handleSearchMobileCancel() {
      if (this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    //清空所选
    handleSearchMobileClearAll() {
      this.nameMobileValue == "" ? "" : (this.nameMobileValue = "");
    },
    //开始搜索
    handleMobileSearch() {
      if (this.nameMobileValue == "") {
        this.$message.error("请输入产品名称");
        return;
      }
      this.mobileSearchButtonLoading = true;
      this.getMobileList();
      this.mobileSearchButtonLoading = false;
      this.mobileSearchShow = false;
    },
    //限制页面跳转输入框只能输入数字
    jumpPageInput() {
      this.pageJumpIndex = this.pageJumpIndex.replace(/[^\d]/g, "");
    },
    //跳转指定页面
    handleJumpPage() {
      let jumpPage = parseInt(this.pageJumpIndex);
      if (jumpPage == this.mobileCurrentPage) return;
      if (jumpPage > Math.ceil(this.listTotal / this.pagesize)) {
        jumpPage = Math.ceil(this.listTotal / this.pagesize);
      }
      if (jumpPage < 1) {
        jumpPage = 1;
      }
      this.pageJumpIndex = jumpPage;
      this.currentPage = jumpPage;
      this.getMobileList();
    }
  }
};
