import { validUsername } from "@/utils/validate";
import { clearTimeout, clearInterval } from "timers";

export default {
  name: "Login",
  data() {
    // const validateUsername = (rule, value, callback) => {
    //   if (!validUsername(value)) {
    //     callback(new Error("请输入正确的用户名"));
    //   } else {
    //     callback();
    //   }
    // };
    const validatePassword = (rule, value, callback) => {
      if (value.length < 6) {
        callback(new Error("密码至少6位数"));
      } else {
        callback();
      }
    };
    return {
      loginForm: {
        username: "",
        password: "",
        verifycode: ""
      },
      loginRules: {
        // username: [
        //   { required: true, trigger: "blur", validator: validateUsername }
        // ],
        password: [
          { required: true, trigger: "blur", validator: validatePassword }
        ],
        verifycode: [{}]
      },
      loading: false,
      passwordType: "password",
      redirect: undefined,
      timecount: 60,
      verifyButtonText: "获取验证码",
      verifycodeLoad: false
    };
  },
  watch: {
    $route: {
      handler: function(route) {
        this.redirect = route.query && route.query.redirect;
      },
      immediate: true
    }
  },
  methods: {
    showPwd() {
      if (this.passwordType === "password") {
        this.passwordType = "";
      } else {
        this.passwordType = "password";
      }
      this.$nextTick(() => {
        this.$refs.password.focus();
      });
    },
    handleLogin() {
      this.$refs.loginForm.validate(valid => {
        if (valid) {
          this.loading = true;
          this.$store
            .dispatch("user/login", this.loginForm)
            .then(() => {
              this.$router.push({ path: this.redirect || "/" });
              this.loading = false;
            })
            .catch(() => {
              this.loading = false;
            });
        } else {
          console.log("出错了!!");
          return false;
        }
      });
    },
    handleGetVerifyCode() {
      this.verifyButtonText = this.timecount + "秒后重新获取";
      this.timecount = this.timecount - 1;
      if(this.timecount == 0){
        this.verifyButtonText = "点击重新获取";
        this.timecount = 60;
        return
      }
      setTimeout(this.handleGetVerifyCode, 1000);
    }
  }
};
