import Vue from "vue";
import {
  getAllChannelList,
  getChannelList,
  addChannel,
  updateChannel,
  deleteChannel,
  deleteSelectChannel
} from "@/api/orderList";
import {
  Pagination,
  Button,
  Popup,
  NavBar,
  Cell,
  CellGroup,
  DatetimePicker,
  Picker,
  Field,
  Icon,
  Toast,
  ActionSheet,
  Search
} from "vant";

Vue.use(Pagination);
Vue.use(Button);
Vue.use(Popup);
Vue.use(NavBar);
Vue.use(Cell);
Vue.use(CellGroup);
Vue.use(DatetimePicker);
Vue.use(Picker);
Vue.use(Field);
Vue.use(Icon);
Vue.use(Toast);
Vue.use(ActionSheet);
Vue.use(Search);

export default {
  data() {
    return {
      list: [],
      nameInput: "",
      formLabelWidth: "120px",
      dialogTableVisible: false,
      currentPage: 1, //当前页
      pagesizes: [20, 40, 60, 80, 100], //单页最大显示条数
      pagesize: 20, //单页内条数
      device: "",
      mobileSearchShow: false,
      mobileSearchButtonLoading: false,
      listLoading: false,
      channelOptions: [],
      editDialogVisible: false,
      dialogTableVisible: false,
      UpdateDialogVisible: false,
      deleteDialogVisible: false,
      deleteSelectDialogVisible: false,
      form: {
        parentChannel: "",
        channelName: ""
      },
      childrenChannelTitle: "",
      childrenChannelList: [],
      childrenCurrentPage: 1, //当前页
      childrenPagesizes: [10, 20, 30, 40, 50], //单页最大显示条数
      childrenPagesize: 10, //单页内条数
      childrenListLoading: false,
      updateForm: {
        id: "",
        name: ""
      },
      pageJumpIndex: 1,
      nameMobileValue: "",
      tableMaxHeight: 0,
      paramsStorage: {},
      multipleSelection: [],
      deleteId: "",
      deleteName: "",
      idsStr: "",
      namesStr: ""
    };
  },
  created() {
    this.list = this.getOrderList();
    this.device = this.$store.state.app.device;
    this.getChannel();
    this.getHeight();
  },
  computed: {
    deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    // 获取表格列表
    getOrderList() {
      let orderList = [];
      this.listLoading = true;
      getAllChannelList({ contains: false, page: 1, rows: 50 }).then(res => {
        let tableList = res.data.rows;
        tableList.forEach(tableItem => {
          const { id, name } = tableItem;
          const orderItem = {
            id: id,
            name: name
          };
          orderList.push(orderItem);
        });
      });
      this.listLoading = false;
      return orderList;
    },
    // 获取渠道列表
    getChannel() {
      getChannelList().then(res => {
        const channelList = res.data;
        channelList.forEach(channelItem => {
          const channelObject = {
            value: channelItem.id,
            label: channelItem.name
          };
          this.channelOptions.push(channelObject);
        });
      });
    },
    // 表格高度自适应
    getHeight() {
      let otherHeight = this.device == "desktop" ? 250 : 200;
      this.tableMaxHeight = window.innerHeight - otherHeight;
    },
    // 选择表格尺寸
    handleSizeChange(val) {
      this.listLoading = true;
      setTimeout(() => {
        this.pagesize = val;
        this.listLoading = false;
      }, 500);
    },
    // 选择表格当前页数
    handleCurrentChange(val) {
      this.listLoading = true;
      setTimeout(() => {
        this.currentPage = val;
        this.listLoading = false;
      }, 500);
    },
    // 单击复制
    handleUseful(row, column, cell, event) {
      if (this.device == "mobile") return;
      if (this.clickFlag) {
        clearTimeout(this.clickFlag);
        this.clickFlag = null;
      }
      this.clickFlag = setTimeout(() => {
        let count = 0;
        if (column.label == undefined) return;
        if (column.label == "操作") {
          this.list.forEach(item => {
            if (item.id == row.id) {
            } else {
              count++;
            }
          });
        } else {
          let copyText = event.target.innerText;
          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");
            this.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            this.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    // 单击选择行
    handleSelect(rows, column, event) {
      if (column.label == "操作") return;
      this.$refs.channelTable.toggleRowSelection(rows);
    },
    // 选择改变
    handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    // 双击查看
    handleChildrenDetail(row, column, event) {
      let orderList = [];
      const { id, name } = row;
      this.childrenChannelTitle = `${name}子渠道`;
      getAllChannelList({
        parentId: id,
        contains: false,
        page: 1,
        rows: 50
      })
        .then(res => {
          let tableList = res.data.rows;
          tableList.forEach(tableitem => {
            const { id, name } = tableitem;
            const orderItem = { id: id, name: name };
            orderList.push(orderItem);
          });
        })
        .catch(error => {
          console.log(error);
        });
      this.childrenChannelList = orderList;
      this.dialogTableVisible = true;
    },
    // 选择子渠道表格尺寸
    handleChildrenSizeChange(val) {
      this.childrenListLoading = true;
      setTimeout(() => {
        this.childrenPagesize = val;
        this.childrenListLoading = false;
      }, 500);
    },
    // 选择子渠道表格当前页数
    handleChildrenCurrentChange(val) {
      this.childrenListLoading = true;
      setTimeout(() => {
        this.childrenCurrentPage = val;
        this.childrenListLoading = false;
      }, 500);
    },
    // 搜索
    handleSearch() {
      let orderList = [];
      const name = this.nameInput;
      if (name == "") {
        this.$message.error("请输入渠道名称");
        return;
      }
      this.listLoading = true;
      this.paramsStorage = { name: name, contains: false, page: 1, rows: 50 };
      getAllChannelList({
        name: name,
        contains: false,
        page: 1,
        rows: 50
      })
        .then(res => {
          let tableList = res.data.rows;
          tableList.forEach(tableItem => {
            const { id, name } = tableItem;
            const orderItem = { id: id, name: name };
            orderList.push(orderItem);
          });
        })
        .catch(error => {
          console.log(error);
        });
      this.listLoading = false;
      this.list = orderList;
    },
    // 新增
    handleAdd() {
      if (!this.editDialogVisible) {
        this.editDialogVisible = true;
      }
    },
    // 取消新增
    handleEditCancel() {
      if (this.editDialogVisible) {
        this.editDialogVisible = false;
      }
    },
    // 新增验证
    addChannelVerify() {
      const { channelName, parentChannel } = this.form;
      if (parentChannel == null || parentChannel == "") {
        this.$message.error("请选择所属渠道");
        return false;
      }
      if (channelName == "") {
        this.$message.error("请填写渠道名");
        return false;
      }
      return true;
    },
    // 确认新增
    handleEditConfirm() {
      if (!this.addChannelVerify()) return;
      const { channelName, parentChannel } = this.form;
      addChannel({ parentId: parentChannel, name: channelName })
        .then(res => {
          if (res.status == 200) {
            this.reloadPage();
            this.$message({
              message: "新增成功",
              type: "success"
            });
          }
        })
        .catch(error => {
          console.log(error);
        });
      this.editDialogVisible = false;
    },
    // 更新
    handleUpdata(row) {
      if (this.device == "mobile") return;
      this.updateForm.id = row.id;
      this.updateForm.name = row.name;
      this.UpdateDialogVisible = true;
    },
    // 更新验证
    updateVerify() {
      const { id, name } = this.updateForm;
      if (name == null || name == "") {
        this.$message.error("请选择渠道");
        return false;
      }
      if (id == "") {
        this.$message.error("请输入ID");
        return false;
      }
      return true;
    },
    // 确认更新
    handleUpdateConfirm() {
      if (!this.updateVerify()) return;
      const { id, name } = this.updateForm;
      updateChannel({ id: id, parentId: "", name: name })
        .then(res => {
          if (res.status == 200) {
            this.reloadPage();
            this.$message({
              message: "更新成功",
              type: "success"
            });
          }
        })
        .catch(error => {
          console.log(error);
        });
      this.UpdateDialogVisible = false;
    },
    // 取消更新
    handleUpdateCancel() {
      if (this.UpdateDialogVisible) {
        this.UpdateDialogVisible = false;
      }
    },
    // 删除
    handleDelete(row) {
      this.deleteId = row.id;
      this.deleteName = row.name;
      if (!this.deleteDialogVisible) {
        this.deleteDialogVisible = true;
      }
    },
    // 确认删除
    handleDeleteConfirm() {
      deleteChannel({ id: this.deleteId }).then(res => {
        if (res.status == 200) {
          this.$message({
            message: "删除成功",
            type: "success"
          }),
            this.reloadPage();
        }
        this.deleteDialogVisible = false;
      });
    },
    // 取消删除
    handleDeleteCancel() {
      this.deleteId = "";
      this.deleteName = "";
      if (this.deleteDialogVisible) {
        this.deleteDialogVisible = false;
      }
    },
    // 批量删除
    handleDeleteSelect() {
      if (this.multipleSelection.length === 0) {
        this.$message.error("未选择任何渠道");
        return;
      }
      let ids = [];
      let names = [];
      this.multipleSelection.forEach(selectItem => {
        ids.push(selectItem.id);
        names.push(selectItem.name);
      });
      this.idsStr = ids.join(",");
      this.namesStr = names.join(",");
      if (!this.deleteSelectDialogVisible) {
        this.deleteSelectDialogVisible = true;
      }
    },
    // 确认批量删除
    handleDeleteSelectConfirm() {
      deleteSelectChannel({ ids: this.idsStr }).then(res => {
        if (res.status == 200) {
          this.$message({
            message: "删除成功",
            type: "success"
          }),
            this.reloadPage();
        }
        this.deleteSelectDialogVisible = false;
      });
    },
    // 取消批量删除
    handleDeleteSelectCancel() {
      this.idsStr = "";
      this.namesStr = "";
      if (!this.deleteSelectDialogVisible) {
        this.deleteSelectDialogVisible = false;
      }
    },
    // 重载页面
    reloadPage() {
      if (JSON.stringify(this.paramsStorage) == "{}") {
        this.list = this.getOrderList();
      } else {
        console.log(1);
        let searchList = [];
        getAllColorList(this.paramsStorage).then(res => {
          const tableData = res.data.rows;
          tableData.forEach(tableItem => {
            const { id, name } = tableItem;
            const orderItem = {
              id: id,
              name: name
            };
            searchList.push(orderItem);
          });
          this.list = searchList;
        });
      }
    },

    /* 移动端事件 */

    //搜索
    handleSearchMobile() {
      if (!this.mobileSearchShow) {
        this.mobileSearchShow = true;
      }
    },
    //清空
    handleSearchMobileClearAll() {
      this.nameMobileValue == "" ? "" : (this.nameMobileValue = "");
    },
    //取消搜索
    handleSearchMobileCancel() {
      if (this.mobileSearchShow) {
        this.mobileSearchShow = false;
      }
    },
    // 开始搜索
    handleMobileSearch() {
      let orderList = [];
      const name = this.nameMobileValue;
      if (name == "") {
        this.$message.error("请输入渠道名称");
        this.mobileSearchShow = false;
        return;
      }
      this.listLoading = true;
      getAllChannelList({
        name: name,
        contains: false,
        page: 1,
        rows: 50
      })
        .then(res => {
          let tableList = res.data.rows;
          tableList.forEach(tableItem => {
            const { id, name } = tableItem;
            const orderItem = { id: id, name: name };
            orderList.push(orderItem);
          });
        })
        .catch(error => {
          console.log(error);
        });
      this.listLoading = false;
      this.mobileSearchShow = false;
      this.list = orderList;
    },
    // 分页器
    handlePageChange() {
      this.listLoading = true;
      setTimeout(() => {
        this.listLoading = false;
      }, 600);
    },
    // 限制页面跳转输入框只能输入数字
    jumpPageInput() {
      this.pageJumpIndex = this.pageJumpIndex.replace(/[^\d]/g, "");
    },
    // 跳转指定页面
    handleJumpPage() {
      let jumpPage = parseInt(this.pageJumpIndex);
      if (jumpPage == this.currentPage) return;
      if (jumpPage > Math.ceil(this.list.length / this.pagesize)) {
        jumpPage = Math.ceil(this.list.length / this.pagesize);
      }
      if (jumpPage < 1) {
        jumpPage = 1;
      }
      this.listLoading = true;
      setTimeout(() => {
        this.pageJumpIndex = jumpPage;
        this.currentPage = jumpPage;
        this.listLoading = false;
      }, 1000);
    }
  }
};
