        }
      ],
      timeSelectValue: "",
      salemanValue: "",
      channelValue: "",
      productValue: "",
      usefulValue: "",
      repeatOrderValue: "",
      repeatNamePhoneValue: "",
      exportjdValue: "",
      colorInput: "",
      nameInput: "",
      phoneNumberInput: "",
      minIdInput: "",
      maxIdInput: "",
      minPriceInput: "",
      maxPriceInput: "",
      formLabelWidth: "120px",
      editDialogVisible: false,
      form: {
        productType: "",
        name: "",
        color: "",
        count: "",
        price: "",
        remarks: "",
        address: ""
      },
      currentEditID: 0,
      clickFlag: null, // 单击定时器
      downloadLoading: false,
      listLoading: true,
      currentPage: 1, //当前页
      pagesizes: [20, 40, 60, 80, 100], //单页最大显示条数
      pagesize: 20 //单页内条数
    };
  },
  created() {
    this.getDataList();
  },
  methods: {
    // 获取数据列表
    getDataList() {
      this.listLoading = true;
      this.list = getOrderList();
      this.listLoading = false;
    },
    // 双击编辑
    handleEdit(e) {
      if (this.clickFlag) {
        this.clickFlag = clearTimeout(this.clickFlag);
      }
      this.form.productType = e.productName;
      this.form.name = e.name;
      this.form.color = e.color;
      this.form.count = e.count;
      this.form.price = e.price;
      this.form.remarks = e.remarks;
      this.form.address = e.address;
      this.currentEditID = e.id;
      this.editDialogVisible = true;
    },
    handleEditCancel() {
      this.editDialogVisible = false;
    },
    handleEditConfirm(row, column, event) {
      let count = 0;
      this.list.forEach(item => {
        if (item.id == this.currentEditID) {
          this.list[count].productName = this.form.productType;
          this.list[count].name = this.form.name;
          this.list[count].color = this.form.color;
          this.list[count].count = this.form.count;
          this.list[count].price = this.form.price;
          this.list[count].remarks = this.form.remarks;
          this.list[count].address = this.form.address;
        } else {
          count++;
        }
      });
      this.editDialogVisible = false;
      this.$message({
        message: "修改成功",
        type: "success"
      });
    },
    handleSearch() {
      console.log(this.$router.options.routes);
    },
    //单击复制
    handleUseful(row, column, cell, event) {7
      if (this.clickFlag) {
        this.clickFlag = clearTimeout(this.clickFlag);
      }
      this.clickFlag = setTimeout(() => {
        let count = 0;
        console.log(row);
        console.log(column);
        if (column.label == "是否可用") {
          this.list.forEach(item => {
            if (item.id == row.id) {
              this.list[count].isUseful = !this.list[count].isUseful;
            } else {
              count++;
            }
          });
        } else {
          let copyText = event.target.innerText;
          if (copyText != "") {
            var inputElement = document.getElementById("copy_content");
            inputElement.value = copyText;
            inputElement.select();
            document.execCommand("Copy");
            this.$message({
              message: "复制成功",
              type: "success"
            });
          } else {
            this.$message.error("复制失败，内容可能为空");
          }
        }
      }, 300);
    },
    //选择表格尺寸
    handleSizeChange(val) {
      this.listLoading = true;
      setTimeout(() => {
        this.pagesize = val;
        this.listLoading = false;
      }, 500);
    },
    //选择表格当前页数
    handleCurrentChange(val) {
      this.listLoading = true;
      setTimeout(() => {
        this.currentPage = val;
        this.listLoading = false;
      }, 500);
    },
    //导出excel
    handleDownload() {
      this.downloadLoading = true;
      import("@/vendor/Export2Excel").then(excel => {
        const tHeader = [
          "id",
          "渠道项目",
          "产品名称",
          "颜色名称",
          "名字",
          "手机",
          "数量",
          "总价",
          "重单",
          "详细地址",
          "创建时间",
          "备注",
          "是否可用",
          "导入物流状态",
          "业务员",
          "操作员",
          "核单间隔"
        ];
        const filterVal = [
          "id",
          "channel",
          "productName",
          "color",
          "name",
          "phoneNumber",
          "count",
          "price",
          "repeatOrder",
          "address",
          "createTime",
          "remarks",
          "isUseful",
          "logisticsState",
          "salesman",
          "operator",
          "nuclearOrderInterval"
        ];
        const list = this.list;
        const data = this.formatJson(filterVal, list);
        excel.export_json_to_excel({
          header: tHeader,
          data,
          filename:
            new Date().toLocaleDateString() + new Date().toLocaleTimeString(),
          autoWidth: true,
          bookType: "xlsx"
        });
        this.downloadLoading = false;
      });
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v =>
        filterVal.map(j => {
          if (j === "timestamp") {
            return parseTime(v[j]);
          } else {
            return v[j];
          }
        })
      );
    }
  }
};
