import FlipItem from "./FlipItem.vue";
import { getTimeArr } from "./utils";
export default {
  name: "FlipClock",
  components: {
    FlipItem
  },
  data() {
    return {
      timeArr: getTimeArr()
    };
  },
  mounted() {
    this.startTimer();
  },
  beforeDestroy() {
    this.stopTimer();
  },
  methods: {
    startTimer() {
      this.stopTimer();
      this.timer = setTimeout(() => {
        this.timeArr = getTimeArr();
        this.startTimer();
      }, 1000);
    },
    stopTimer() {
      clearTimeout(this.timer);
    }
  }
};
