import Vue from "vue";
import permission from "@/directive/permission/index.js"; // 权限判断指令
import checkPermission from "@/utils/permission"; // 权限判断函数
import {
  getRoleList,
  addRoleInquired,
  addRole,
  updateRoleInquired,
  updateRole,
  deleteRole,
  deleteSelectRole
} from "@/api/orderList";
import {
  Pagination,
  Button,
  Popup,
  NavBar,
  Cell,
  CellGroup,
  DatetimePicker,
  Picker,
  Field,
  Icon,
  Toast,
  ActionSheet,
  Search
} from "vant";

Vue.use(Pagination);
Vue.use(Button);
Vue.use(Popup);
Vue.use(NavBar);
Vue.use(Cell);
Vue.use(CellGroup);
Vue.use(DatetimePicker);
Vue.use(Picker);
Vue.use(Field);
Vue.use(Icon);
Vue.use(Toast);
Vue.use(ActionSheet);
Vue.use(Search);

export default {
  name: "system-role",
  directives: { permission },
  data() {
    return {
      list: [],
      usernameInput: "",
      usefulValue: "",
      usefulOptions: [
        {
          value: "1",
          label: "是"
        },
        {
          value: "0",
          label: "否"
        }
      ],
      usefulColumns: ["是", "否"],
      formLabelWidth: "120px",
      dialogTableVisible: false,
      currentPage: 1, //当前页
      mobileCurrentPage: 1,
      pagesizes: [100, 200, 500], //单页最大显示条数
      pagesize: 100, //单页内条数
      listTotal: 0, //总数
      device: "",
      mobileSearchShow: false,
      mobileSearchButtonLoading: false,
      searchButtonLoading: false,
      tableMaxHeight: 0,
      addDialogVisible: false,
      updateDialogVisible: false,
      form: {
        checkList: [],
        name: ""
      },
      updateForm: {
        checkList: [],
        username: "",
        name: "",
        password: "",
        telephone: "",
        mode: ""
      },
      updatePermissionList: [],
      listLoading: false,
      multipleSelection: [],
      updateId: "",
      treeData: [],
      defaultProps: {
        children: "children",
        label: "name"
      },
      updateTreeData: [],
      pageJumpIndex: 1,
      nameMobileValue: ""
    };
  },
  created() {
    this.getList();
    this.device = this.$store.state.app.device;
    this.getHeight();
  },
  computed: {
    deviceVal() {
      return this.$store.state.app.device;
    }
  },
  watch: {
    deviceVal(newVal, oldVal) {
      this.device = newVal;
    }
  },
  methods: {
    checkPermission,
    // 获取数据
    getList() {
      this.listLoading = true;
      let searchList = [];
      let paramObj = {
        page: this.currentPage,
        rows: this.pagesize
      };
      this.usernameInput ? (paramObj.name = name) : "";
      getRoleList(paramObj).then(res => {
        const tableList = res.data.rows;
        tableList.forEach(tableItem => {
          const { id, name } = tableItem;
          const orderItem = {
            id: id,
            name: name
          };
          searchList.push(orderItem);
        });
        this.list = searchList;
      });
      setTimeout(() => {
        this.listLoading = false;
      }, 1000);
    },
    //表格高度自适应
    getHeight() {
      let otherHeight = this.device == "desktop" ? 250 : 200;
      this.tableMaxHeight = window.innerHeight - otherHeight;
    },
    // 页面条数切换
    handleSizeChange(val) {
      this.pagesize = val;
      this.getList();
    },
    //选择表格当前页数
    handleCurrentChange(val) {
      this.currentPage = val;
      this.getList();
    },
    // 选择改变
    handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    // 搜索
    handleSearch() {
      this.searchButtonLoading = true;
      this.getList();
      this.searchButtonLoading = false;
    },
    // 新增
    handleAdd() {
      addRoleInquired().then(res => {
        this.treeData = res.data;
      });
      this.addDialogVisible = true;
    },
    // 权限选择变化时
    handlePermisionCheck(current, status) {
      this.form.checkList = status.checkedKeys;
    },
    // 取消新增
    handleAddCancel() {
      this.addDialogVisible = false;
    },
    // 验证新增
    addVerify() {
      return true;
    },
    // 确认新增
    handleAddConfirm() {
      if (!this.addVerify()) return;
      this.listLoading = true;
      let name = this.form.name;
      let functionId = this.form.checkList.join(",");
      addRole({
        functionId: functionId,
        name: name
      })
        .then(res => {
          if (res.status === 200) {
            this.getList();
            this.$message({
              type: "success",
              message: "添加用户成功"
            });
          } else {
            this.$message.error("添加失败");
          }
        })
        .catch(error => {
          console.log(error);
        });
      this.listLoading = false;
      this.addDialogVisible = false;
    },
    // 更新
    handleUpdata(rows) {
      updateRoleInquired({ id: rows.id }).then(res => {
        this.updateId = rows.id;
        this.updateForm.name = res.data.role.name;
        this.updateForm.checkList = res.data.functiona;
        this.updateTreeData = res.data.functionList;
      });
      this.updateDialogVisible = true;
    },
    // 权限选择变化时
    handleUpdatePermisionCheck(current, status) {
      this.updateForm.checkList = status.checkedKeys;
    },
    // 取消更新
    handleUpdateCancel() {
      this.updateDialogVisible = false;
    },
    // 验证更新
    updateVerify() {
      return true;
    },
    // 确认更新
    handleUpdateConfirm() {
      if (!this.updateVerify()) {
        return;
      }
      this.listLoading = true;
      let id = this.updateId;
      let name = this.updateForm.name;
      let functionId = this.updateForm.checkList.join(",");
      updateRole({
        id: id,
        functionId: functionId,
        name: name
      })
        .then(res => {
          if (res.status === 200) {
            this.getList();
            this.$message({
              type: "success",
              message: "更新用户成功"
            });
          } else {
            this.$message.error("更新失败");
          }
        })
        .catch(error => {
          console.log(error);
        });
      this.listLoading = false;
      this.updateDialogVisible = false;
    },
    // 删除
    handleDelete(rows) {
      this.$confirm("此操作将永久删除该角色, 是否继续?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          this.listLoading = true;
          deleteRole({ id: rows.id }).then(res => {
            if (res.status === 200) {
              this.getList();
              this.$message({
                type: "success",
                message: "删除角色成功"
              });
            } else {
              this.$message.error("删除失败");
            }
          });
          this.listLoading = false;
        })
        .catch(() => {
          this.$message({
            type: "info",
            message: "已取消删除"
          });
        });
    },
    // 批量删除
    handleDeleteSelect() {
      this.$confirm("此操作将永久删除该角色, 是否继续?", "提示", {
        confirmButtonText: "确定",
        cancelButtonText: "取消",
        type: "warning"
      })
        .then(() => {
          if (this.multipleSelection.length === 0) {
            this.$message.error("未选择数据");
            return;
          }
          let ids = [];
          this.multipleSelection.forEach(selectItem => {
            ids.push(selectItem.id);
          });
          let idsStr = ids.join(",");
          deleteSelectRole({ ids: idsStr }).then(res => {
            if (res.status === 200) {
              this.getList();
              this.$message({
                type: "success",
                message: "批量删除角色成功"
              });
            } else {
              this.$message.error("删除失败");
            }
          });
        })
        .catch(() => {
          this.$message({
            type: "info",
            message: "已取消删除"
          });
        });
    },

    /* 移动端事件 */

    // 获取数据
    getMobileList() {
      this.listLoading = true;
      let searchList = [];
      let paramObj = {
        page: this.mobileCurrentPage,
        rows: this.pagesize
      };
      this.nameMobileValue ? (paramObj.name = this.nameMobileValue) : "";
      getRoleList(paramObj).then(res => {
        console.log(res)
        this.listTotal = res.data.total;
        const tableList = res.data.rows;
        tableList.forEach(tableItem => {
          const { id, name } = tableItem;
          const orderItem = {
            id: id,
            name: name
          };
          searchList.push(orderItem);
        });
        this.list = searchList;
      });
      setTimeout(() => {
        this.listLoading = false;
      }, 1000);
    },
    // 搜索
    handleSearchMobile() {
      if (!this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    // 取消搜索
    handleSearchMobileCancel() {
      if (this.mobileSearchShow) {
        this.mobileSearchShow = !this.mobileSearchShow;
      }
    },
    // 确认搜索
    handleMobileSearch() {
      this.mobileSearchButtonLoading = true;
      this.getMobileList();
      this.mobileSearchButtonLoading = false;
      this.mobileSearchShow = false;
    },
    // 清空所选
    handleSearchMobileClearAll() {
      this.nameMobileValue = "";
    },
    // 分页器改变
    handlePageChange() {
      this.getMobileList();
    },
    // 限制页面跳转输入框只能输入数字
    jumpPageInput() {
      this.pageJumpIndex = this.pageJumpIndex.replace(/[^\d]/g, "");
    },
    // 跳转指定页面
    handleJumpPage() {
      let jumpPage = parseInt(this.pageJumpIndex);
      if (jumpPage == this.mobileCurrentPage) return;
      if (jumpPage > Math.ceil(this.listTotal / this.pagesize)) {
        jumpPage = Math.ceil(this.listTotal / this.pagesize);
      }
      if (jumpPage < 1) {
        jumpPage = 1;
      }
      this.pageJumpIndex = jumpPage;
      this.mobileCurrentPage = jumpPage;
      this.getMobileList();
    }
  }
};
